function onload(saved_state)
    if saved_state ~= nil and saved_state ~= "" then
        savedData = JSON.decode(saved_state)
        loadSavedValues()
    end
    if savedData.manualMode then
        createScoreDisplayXmlMenu()
        return
    end
    createTableXmlMenu()
    if savedData.phase == 0 then
        freezeEverything()
    else
        Wait.frames(function()
            hideTableSetupMenu()
        end, 30)
        createScoreDisplayXmlMenu()
        prepareEverything()
    end
    keepCurrentPlayerTurn()
    Wait.frames(function() 
        computerBlink() 
        toggleExpansions(true)
    end, 10)
end

function prepareEverything()
    for _, object in ipairs(getAllObjects()) do
        object.setLock(true)
        object.interactable = false
    end
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            prepareTile(tile)
            for m, meeple in ipairs(tile.meeples) do
                prepareMeeple(meeple)
            end
            for m, meeple in ipairs(tile.pendingMeeples) do
                prepareMeeple(meeple)
            end
            if tile.card ~= nil then
                prepareWhimCard(tile.card)
            end
        end
    end
    for d, djinn in ipairs(savedData.djinns) do
        if djinn ~= '' then
            prepareDjinn(djinn)
        end
    end
    for r, resource in ipairs(savedData.resources) do
        prepareResource(resource)
    end
    for i, item in ipairs(savedData.itemsDiscard) do
        prepareItem(item)
    end
    if getObjectFromGUID(savedData.turnTrack) ~= nil then
        prepareTurnOrderTrack(savedData.turnTrack)
    end
    if savedData.yazid ~= nil then
        table.insert(savedData.players, savedData.yazid)
    end
    for p, player in ipairs(savedData.players) do
        for m, meeple in ipairs(player.selectedMeeples) do
            prepareMeeple(meeple)
        end
        for m, meeple in ipairs(player.meeples) do
            prepareMeeple(meeple)
        end
        for d, djinn in ipairs(player.djinns) do
            prepareDjinn(djinn.guid)
        end
        for r, resource in ipairs(player.resources) do
            prepareResource(resource)
        end
        for i, item in ipairs(player.items) do
            prepareItem(item, player)
        end
        for w, whim in ipairs(player.whims) do
            prepareWhimCard(whim.guid)
        end
        preparePlayerBoard(player)
        Wait.frames(function()
            countGold(player)
            arrangePlayerResources(player)
            arrangePlayerItems(player)
            if player.action ~= nil then
                showPlayerUI(player)
            end
        end, 30)
    end
end

