    freezeGuid(tableRoundGuid)
    freezeGuid(meepleBagGuid)
    freezeGuid(resourceDeckGuid)
    freezeGuid(goldPileGuid)
    freezeGuid(fiveGoldBagGuid)
    freezeGuid(oneGoldBagGuid)
    freezeGuid(itemsBagGuid, true, true)
    for t, tribe in ipairs(tribes) do
        freezeGuid(tribe.bagGuid)
    end
    freezeGuid(tribes[3].altBagGuid)
    for _, info in ipairs(playerInfo) do
        freezeGuid(info.compHandGuid)
        freezeGuid(info.board)
        freezeGuid(info.turnHighlightGuid)
    end
    for p, palmTree in ipairs(palmTreeGuids) do
