--Setup
manualModeWarning = false
function manualMode(player, mouse, id, isHost)
    if isHost then
        if manualModeWarning then
            savedData.manualMode = true
            getObjectFromGUID(tableGuid).UI.setXml("")
            createScoreDisplayXmlMenu()
            for _, object in ipairs(getAllObjects()) do
                object.interactable = true
            end
            unlockGuid(firstPlayerMarkerGuid)
            for r, row in ipairs(savedData.board) do
                for c, tile in ipairs(row) do
                    if not noScripting then
                        getObjectFromGUID(tile.guid).UI.setXml('')
                    end
                    for m, meeple in ipairs(tile.meeples) do
                        unlockGuid(meeple, true, highlightMeeple)
                    end
                    for m, meeple in ipairs(tile.pendingMeeples) do
                        unlockGuid(meeple, true, true)
                    end
                    for b, building in ipairs(tile.buildings) do
                        unlockGuid(building)
                    end
                    unlockGuid(tile.camel, false, true)
                    unlockGuid(tile.card, true)
                end
            end
            for d, djinn in ipairs(savedData.djinns) do
                unlockGuid(djinn, true)
            end
            unlockGuid(savedData.djinnDeck)
            for r, resource in ipairs(savedData.resources) do
                unlockGuid(resource, true)
            end
            unlockGuid(savedData.resourceDiscard)
            for p, palmTree in ipairs(savedData.palmTrees) do
                unlockGuid(palmTree)
            end
            for p, palace in ipairs(savedData.palaces) do
                unlockGuid(palace)
            end
            for i, item in ipairs(savedData.items) do
                destroy(item.clone)
                unlockGuid(item.guid)
            end
            for i, item in ipairs(savedData.itemsDiscard) do
                destroy(item.clone)
                unlockGuid(item.guid, true)
                getObjectFromGUID(item.guid).putObject(getObjectFromGUID(itemsBagGuid))
            end
            freezeGuid(itemsBagGuid, false, false)
            if getObjectFromGUID(savedData.turnTrack) ~= nil then
                getObjectFromGUID(savedData.turnTrack).UI.setXml('')
            end
            if savedData.yazid ~= nil then
                table.insert(savedData.players, savedData.yazid)
            end
            for p, player in ipairs(savedData.players) do
                if hiddenInformationOption ~= 3 then
                    local tPos = getTransformPosition(getObjectFromGUID(player.board),{
                        x = 0, 
                        y = 3, 
                        z = 7.5
                    })
                    tPos.y = 3
                    local rot = getObjectFromGUID(player.board).getRotation()
                    local hiddenZone = spawnObject({
                        type = "FogOfWarTrigger",
                        position          = tPos,
                        rotation          = rot,
                        scale             = {x=35, y=4, z=11}
                    })
                    hiddenZone.setValue(player.color)
                    Wait.time(function()
                        hiddenZone.setRotation(rot)
                    end, setupSpeed)
                end
                for m, meeple in ipairs(player.meeples) do
                    unlockGuid(meeple, true, not noScripting)
                end
                for d, djinn in ipairs(player.djinns) do
                    unlockGuid(djinn.guid, true)
                end
                for r, resource in ipairs(player.resources) do
                    unlockGuid(resource, true)
                    getObjectFromGUID(resource).setHiddenFrom({})
                end
                for m, marker in ipairs(player.markers) do
                    unlockGuid(marker, true)
                end
                for c, coin in ipairs(player.coins) do
                    unlockGuid(coin, true)
                end
                for c, camel in ipairs(player.camels) do
                    unlockGuid(camel, true)
                end
                for i, item in ipairs(player.items) do
                    destroy(item.clone)
                    unlockGuid(item.guid, true)
                    getObjectFromGUID(item.guid).setInvisibleTo({})
                    setVisiblity(getObjectFromGUID(item.guid), item.guid .. "Btn", "")
                end
                for w, whim in ipairs(player.whims) do
                    unlockGuid(whim.guid, true)
                end
                unlockGuid(player.whimDiscard)
                getObjectFromGUID(player.board).UI.setXml("")
                freezeGuid(player.board)
            end
            for i, info in ipairs(playerInfo) do
                destroy(info.turnHighlightGuid)
            end
            freezeGuid(tableGuid)
            freezeGuid(tableRoundGuid)
        else
            manualModeWarning = true
            broadcastToAll("!WARNING! Manual Mode will make everything interactable and remove all scripts. It cannot be undone. Double click to enable.")
            Wait.time(function()
                manualModeWarning = false
            end, setupSpeed*2)
        end
    else
        broadcastToPlayer("Only the host can enable Manual Mode", player.color)
    end
end

function unlockGuid(guid, removeUI, highlight)
    if guid ~= nil and getObjectFromGUID(guid) ~= nil then
        local obj = getObjectFromGUID(guid)
        obj.setLock(false)
        if removeUI == true then
            obj.UI.setXml("")
        end
        if highlight == true then
            obj.highlightOn("Black")
        end
    end
end

function setupGame()
    savedData.players = {}
    if gameMode > 1 then
        savedData.hotseatEnabled = false
        if not isPlayerSeated("Pink") then
            Player.getPlayers()[1].changeColor("Pink")
            adjustPlayerItems()
        end
        local info = isPlayerSeated("Pink", true)
        table.insert(savedData.players, addPlayer(info))
        local yazidInfo = playerInfo[1]
        yazidInfo.steam_name = "Yazid the First"
        savedData.yazid = addPlayer(yazidInfo)
        resetDicePosition()
    else
        for p, info in ipairs(playerInfo) do
            if isComputerEnabled(info.color) then
                info.steam_name = info.color .. " Dummy"
                table.insert(savedData.players, addPlayer(info))
            elseif isPlayerSeated(info.color) then
                info = isPlayerSeated(info.color, true)
                if not string.match(info.steam_name, "Player") then
                    savedData.hotseatEnabled = false
                end
                table.insert(savedData.players, addPlayer(info))
            end
        end
        if #savedData.players < 1 then
            broadcastToAll("Need at least 1 player seated to start...", messageColor)
            return
        end
    end
    hideTableSetupMenu()
    createScoreDisplayXmlMenu()
    destroyUnusedItems()
    broadcastToAll("Setting up game for " .. #savedData.players .. " players...", messageColor)
    adjustComputersForSeatedPlayers("hideAll")
    fillMeepleBag()
    savedData.board = createBoard()
    while checkBoardBlockedOff(savedData.board) do
        savedData.board = createBoard()
    end
    savedData.palmTrees = palmTreeGuids
    savedData.palaces = palaceGuids
    groupDjinn()
    getObjectFromGUID(resourceDeckGuid).randomize()
    savedData.resourceDeck = resourceDeckGuid
    adjustObjForBoardOffset(resourceDeckGuid, 2.5)
    if noBidding == 1 and gameMode == 1 then
        if #savedData.players < 5 then
            savedData.bidTrack = bidOrderTrackGuids[1]
            savedData.turnTrack = turnOrderTrackGuids[1]
        else
            savedData.bidTrack = bidOrderTrackGuids[2]
            savedData.turnTrack = turnOrderTrackGuids[2]
        end
        prepareTurnOrderTrack(savedData.turnTrack)
        adjustObjForBoardOffset(savedData.bidTrack)
        adjustObjForBoardOffset(savedData.turnTrack)
    end
    if expansionArtisans then
        savedData.items = table.randomize(items)
        arrangeItems()
    end
    if expansionWhims then
        if expansionArtisans then
            local whimsDeckObj = group({getObjectFromGUID(whimsDeckGuid), getObjectFromGUID(whimsArtisanDeckGuid)})[1]
            Wait.frames(function()
                savedData.whimsDeck = whimsDeckObj.guid
            end, 5)
        else
            savedData.whimsDeck = whimsDeckGuid
        end
        Wait.frames(function()
            getObjectFromGUID(savedData.whimsDeck).randomize()
        end, 10)
    end
end

function arrangeItems()
    local start = {x = -9, y = 1, z = 52}
    for i, item in ipairs(savedData.items) do
        local itemObj = getObjectFromGUID(item.guid)
        local cloneObj = getObjectFromGUID(item.clone)
        itemObj.setPositionSmooth(start)
        itemObj.UI.setXml("")
        cloneObj.setPositionSmooth(start)
        start.y = start.y + 0.1134
    end
end

function adjustObjForBoardOffset(guid, offsetX)
    local obj = getObjectFromGUID(guid)
    local pos = obj.getPosition()
    pos.z = pos.z - savedData.boardOffset
    if offsetX ~= nil then
        pos.x = pos.x + offsetX
    end
    obj.setPositionSmooth(pos, false, true)
end

function groupDjinn()
    local djinnDecksObj = {}
    local djinnDeckOffset = 3.75
    for d, djinn in ipairs(djinnDecks) do
        if djinn.toggle == nil or _G[djinn.toggle] then
            table.insert(djinnDecksObj, getObjectFromGUID(djinn.guid))
        end
    end
    djinnDecksObj = group(djinnDecksObj)[1]
    djinnDecksObj.setLock(true)
    djinnDecksObj.interactable = false
    djinnDecksObj.randomize()
    if expansionThieves then
        djinnDeckOffset = djinnDeckOffset - 3.75*2
        local pos = djinnDecksObj.getPosition()
        pos.x = -18.75
        --djinnDecksObj.setPositionSmooth(pos, false, true)
        adjustObjForBoardOffset(thievesDeckGuid, 3.75)
        if expansionArtisans then
            local thievesDeckObj = group({getObjectFromGUID(thievesDeckGuid), getObjectFromGUID(thiefMadjaarGuid)})[1]
            Wait.frames(function()
                savedData.thievesDeck = thievesDeckObj.guid
            end, 5)
        else
            savedData.thievesDeck = thievesDeckGuid
        end
        Wait.frames(function()
            getObjectFromGUID(savedData.thievesDeck).randomize()
            Wait.frames(function()
                savedData.thievesDeckPos = getObjectFromGUID(savedData.thievesDeck).getPosition()
            end, 60)
        end, 10)
    end
    Wait.frames(function()
        adjustObjForBoardOffset(djinnDecksObj.guid, djinnDeckOffset)
        savedData.djinnDeck = djinnDecksObj.guid
    end, 5)
end

function destroyUnusedItems()
    for _, obj in ipairs(getAllObjects()) do
        if obj.getPosition().y < 0 then
            obj.destroy()
        end
    end
end

function fillMeepleBag()
    local meepleBagObj = getObjectFromGUID(meepleBagGuid)
    local highestTotal = 0
    local speed = setupSpeed/10
    for t, tribe in ipairs(tribes) do
        local total = tribe.amount
        if t ~= 6 and expansionWhims then
            total = total + 3
        elseif t == 6 and not expansionArtisans then
            break
        end
        if total > highestTotal then
            highestTotal = total
        end
        local bagObj = getObjectFromGUID(tribe.bagGuid)
        if altGreenMerchant and t == 3 then
            bagObj = getObjectFromGUID(tribe.altBagGuid)
        end
        for i = 1, total do
            Wait.time(function()
                meepleBagObj.putObject(bagObj.takeObject())
                scaleBag(meepleBagObj, 114)
                if i == total then
                    bagObj.destroy()
                end
            end, speed*i)
        end
    end
    Wait.time(function()
        dealMeeplesTiles()
    end, speed*(highestTotal+1))
end

function dealMeeplesTiles()
    local meepleBagObj = getObjectFromGUID(meepleBagGuid)
    meepleBagObj.randomize()
    savedData.totalMeeples = meepleBagObj.getQuantity()
    local pos = meepleBagObj.getPosition()
    local speed = setupSpeed/20
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            tile.meeples = {}
            if tile.points ~= nil then
                for i = 1, 3 do
                    Wait.time(function()
                        local meepleObj = meepleBagObj.takeObject()
                        meepleObj.setLock(true)
                        meepleObj.interactable = false
                        pos.y = 10*(meepleBagObj.getQuantity()/90)
                        meepleObj.setPositionSmooth(pos, false, true)
                        Wait.frames(function()
                            table.insert(tile.meeples, meepleObj.guid)
                            prepareMeeple(meepleObj.guid)
                            arrangeTileItems(tile)
                        end, 5)
                        scaleBag(meepleBagObj, 114) 
                    end, speed*(r*6+c+i))
                end
            end
        end
    end
    if sameMeepleSetup then
        Wait.time(checkForSameMeeples, speed*(meepleBagObj.getQuantity()))
    end
    Wait.time(function()
        if gameMode > 1 then
            savedData.yazid.tookTurn = true
            savedData.yazid.final = false
            moveFirstPlayerToken(savedData.yazid)
        elseif noBidding ~= 1 then
            savedData.currentTurn = savedData.players[math.random(1, #savedData.players)].color
        else
            local markerList = {}
            for p, player in ipairs(savedData.players) do
                for m, marker in ipairs(player.markers) do
                    table.insert(markerList, {
                        color = player.color,
                        guid = marker
                    })
                end
            end
            while #markerList > 0 do
                table.insert(savedData.bidOrder, table.remove(markerList, math.random(#markerList)))
            end
            arrangeBidTrack()
        end
        savedData.resourceDeckPos = getObjectFromGUID(savedData.resourceDeck).getPosition()
        savedData.resourceDeckRot = getObjectFromGUID(savedData.resourceDeck).getRotation()
        setNextTurn()
    end, speed*(meepleBagObj.getQuantity()/2))
end

function checkForSameMeeples()
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if tile.points ~= nil then
                local color = getObjectFromGUID(tile.meeples[1]).getName()
                if getObjectFromGUID(tile.meeples[2]).getName() == color and
                        getObjectFromGUID(tile.meeples[3]).getName() == color then
                    swapMeeple(r, c, color)
                end
            end
        end
    end
    arrangeAllTiles()
end

function arrangeAllTiles()
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            arrangeTileItems(tile)
        end
    end
end

function swapMeeple(rowC, colC, color)
    local tempBoard = table.copy(savedData.board)
    for r, row in ipairs(tempBoard) do
        for c, tile in ipairs(row) do
            if tile.meeples ~= nil then
                local sameColor = false
                for m, meeple in ipairs(tile.meeples) do
                    if color == getObjectFromGUID(meeple).getName() then
                        sameColor = true
                        break
                    end
                end
                if sameColor == false then
                    savedData.board[rowC][colC].meeples[1] = tile.meeples[1]
                    savedData.board[r][c].meeples[1] = tempBoard[rowC][colC].meeples[1]
                    return
                end
            end
        end
    end
end

function moveFirstPlayerToken(player)
    local boardObj = getObjectFromGUID(player.board)
    local pos = {x = 0, y = 0, z = -15.55}
    local tPos = getTransformPosition(boardObj, pos)
    tPos.y = 2.56
    getObjectFromGUID(firstPlayerMarkerGuid).setPositionSmooth(tPos, false, true)
end

function createBoard()
    local tileSet = createTileSet()
    local start = {x = -18, y = 1, z = 21.6}
    local offset = 7.2
    local board = {}
    local rows = (#tileSet.outerTiles + #tileSet.innerTiles)/6
    if rows == 6 then
        savedData.boardOffset = offset/2
    elseif rows == 5 then
        savedData.boardOffset = offset
    end
    start.z = start.z - savedData.boardOffset
    if expansionArtisans then
        for m, mountain in ipairs(mountainsGuids) do
            local mountainObj = getObjectFromGUID(mountain)
            local pos = mountainObj.getPosition()
            pos.y = 5
            pos.z = pos.z + 20
            mountainObj.setPositionSmooth(pos, false, true)
        end
    end
    for r = 1, rows do
        table.insert(board, {})
        for c = 1, 6 do
            if #tileSet.innerTiles > 0 and r > 1 and c > 1 and r < rows and c < 6 then
                table.insert(board[r], table.remove(tileSet.innerTiles, 1))
            else
                table.insert(board[r], table.remove(tileSet.outerTiles, 1))
            end
            board[r][c].row = r
            board[r][c].col = c
            board[r][c].pendingMeeples = {}
            board[r][c].buildings = {}
            local tileObj = getObjectFromGUID(board[r][c].guid)
            local pos = tileObj.getPosition()
            pos.y = 5
            pos.z = pos.z + 20
            tileObj.setPositionSmooth(pos, false, true)
            Wait.time(function()
                pos = {x = start.x + offset*(c-1), y = start.y, z = start.z - offset*(r - 1)}
                if board[r][c].name == 'Workshop' then
                    if board[r][c].mountainLeft == true then
                        board[r][c].mountainLeft = placeMountain(pos, -offset/2, 0, 90)
                    end
                    if board[r][c].mountainRight == true then
                        board[r][c].mountainRight = placeMountain(pos, offset/2, 0, 270)
                    end
                    if board[r][c].mountainUp == true then
                        board[r][c].mountainUp = placeMountain(pos, 0, offset/2, 0, 180)
                    end
                    if board[r][c].mountainDown == true then
                        board[r][c].mountainDown = placeMountain(pos, 0, -offset/2, 0)
                    end
                end
                prepareTile(board[r][c])
                tileObj.setPositionSmooth(pos, false, true)
            end, setupSpeed/12*(r*6+c))
        end
    end
    return board
end

function checkBoardBlockedOff(board)
    local dirs = {{1,0},{0,1},{-1,0},{0,-1}}
    for r = 2, #board - 1 do
        for c = 2, 5 do
            local blockedCount = 0
            for d, dir in ipairs(dirs) do
                local currentTile = board[r][c]
                local adjacentTile = board[r+dir[1]][c+dir[2]]
                if isBlockedByMountains(currentTile, adjacentTile)
                    or isBlockedByMountains(adjacentTile, currentTile)
                    or adjacentTile.points == nil then
                    blockedCount = blockedCount + 1
                end
            end
            if blockedCount == 4 then
                return true
            end
        end
    end
    return false
end

function placeMountain(tilePos, offsetX, offsetZ, rotY)
    local mountainObj = getObjectFromGUID(table.remove(mountainsGuids, 1))
    local mountainPos = table.copy(tilePos)
    mountainPos.x = mountainPos.x + offsetX
    mountainPos.z = mountainPos.z + offsetZ
    local mountainRot = mountainObj.getRotation()
    mountainRot.y = rotY
    mountainObj.setRotationSmooth(mountainRot, false, true)
    mountainObj.setPositionSmooth(mountainPos, false, true)
    return mountainObj.guid
end

function createTileSet()
    local outerTiles = {}
    local innerTiles = {}
    local workshopCount = 0
    for t, tile in ipairs(tiles) do
        if tile.set == 'base' 
            or (tile.set == 'artisans' and expansionArtisans)
            or (tile.set == 'whims' and expansionWhims) then
            for g, guid in ipairs(tile.guids) do
                local points = nil
                if tile.points ~= nil then
                    if #tile.points > 1 then
                        points = tile.points[g]
                    else
                        points = tile.points[1]
                    end
                end
                local color = nil
                if tile.color ~= nil then
                    if #tile.color > 1 then
                        color = tile.color[g]
                    else
                        color = tile.color[1]
                    end
                end
                local tileInfo = {
                    name = tile.name,
                    guid = guid,
                    func = tile.func,
                    points = points,
                    color = color
                }
                if tile.set == 'artisans' then
                    if tile.name == 'Workshop' then
                        if workshopCount < 2 then
                            tileInfo.mountainRight = true
                            tileInfo.mountainDown = true
                            workshopCount = workshopCount + 1
                        else
                            tileInfo.mountainUp = true
                            tileInfo.mountainLeft = true
                        end
                    end
                    table.insert(innerTiles, tileInfo)
                else
                    table.insert(outerTiles, tileInfo)
                end
            end
        end
    end
    outerTiles = table.randomize(outerTiles)
    if expansionArtisans then
        local total = 10
        if expansionWhims then
            total = 14
        end
        for i = 1, total do
            table.insert(innerTiles, table.remove(outerTiles, 1))
        end
        innerTiles = table.randomize(innerTiles)
    end
    return {outerTiles = outerTiles, innerTiles = innerTiles}
end

function prepareTiles()
