function addPlayer(player)
    local playerInfo = getPlayerInfoIndexFromId(player.color)
    local player = {
        name = player.steam_name,
        color = player.color, 
        turnHighlight = playerInfo.turnHighlightGuid,
        rotation = playerInfo.handRot+180,
        board = playerInfo.board,
        markers = {playerInfo.markerGuid[1]},
        meeples = {},
        camels = {},
        resources = {},
        djinns = {},
        items = {},
        coins = {},
        goldTotal = 0,
        whims = {},
        selectedTiles = {},
        selectedMeeples = {},
        stolenItems = {},
        waitForThief = 0,
        scores = {'','','','','','','','','',''},
        scoreTotal = 0
    }
    preparePlayerBoard(player)
    Wait.frames(function()
        local total = 8
        if #savedData.players == 2 or gameMode ~= 1 then
            total = 11
            table.insert(player.markers, playerInfo.markerGuid[2])
        end
        local camelBagObj = getObjectFromGUID(playerInfo.camelBagGuid)
        local speed = setupSpeed/5
        for i = 1, total do
            Wait.time(function()
                local camelObj = camelBagObj.takeObject()
                camelObj.setLock(true)
                camelObj.interactable = false
                camelObj.setRotation({x = 0, y = player.rotation-90, z = 0})
                Wait.frames(function()
                    table.insert(player.camels, camelObj.guid)
                    arrangeCamels(player)
                end, 10)
            end, speed*i)
        end
        Wait.time(function()
            if expansionArtisans then
                Wait.frames(function()
                    table.insert(player.camels, playerInfo.tentGuid)
                    arrangeCamels(player)
                end, 10)
            end
            camelBagObj.destroy()
        end, speed*(total+1))
        if gameMode == 1 then
            takeGoldCoins(player, 50)
        elseif player.color == "Pink" then
            if gameMode == 2 then 
                takeGoldCoins(player, 25)
            elseif gameMode == 3 then
                takeGoldCoins(player, 20)
            end
        end
    end, 10)
    return player
end

