--Round Start
function startRound()
    refillDjinn()
    refillResources()
    if expansionWhims then
        refillWhimsCards()
    end
end

function refillWhimsCards()
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if tile.name == "Fabulous City" and tile.card == nil then
                if savedData.whimsDeck == nil then
                    return
                end
                local cardDraw = drawCard(savedData.whimsDeck)
                if cardDraw.deckObj ~= nil then
                    Wait.frames(function()
                        savedData.whimsDeck = cardDraw.deckObj.guid
                    end, 10)
                else
                    savedData.whimsDeck = nil
                end
                local cardObj = cardDraw.cardObj
                cardObj.setLock(true)
                cardObj.interactable = false
                local pos = getObjectFromGUID(tile.guid).getPosition()
                pos.y = pos.y + 0.17
                pos.x = pos.x - 1
                Wait.frames(function()
                    cardObj.setRotation({x = 0, y = 180, z = 0})
                    cardObj.setPositionSmooth(pos, false, true)
                    prepareWhimCard(cardObj.guid)
                    tile.card = cardObj.guid
                    arrangeTileItems(tile)
                end, 10)
            end
        end
    end
end

function prepareWhimCard(card)
    getObjectFromGUID(card).UI.setXmlTable({createButton(
        card, "0 0 -100",
        "0 0 0", "1 1 1", {360, 231}, {"", 0, ""},
        "", "", "clickWhimCard", true, "mouseEnterWhimCard", "mouseExitWhimCard", {"","0 0"}
    )})
end

function clickWhimCard(player, isTurn, id)
    if checkIsTurn(player, isTurn) then
        local tile = getTileFromWhimId(id)
        if tile ~= nil then
            clickTile(player, true, tile.guid)
        end
    end
end

function mouseEnterWhimCard(player, mouse, id)
    player = getPlayerFromColor(player.color, true)
    if player ~= nil and player.color == savedData.currentTurn then
        getObjectFromId(id).highlightOn(player.color)
        local tile = getTileFromWhimId(id)
        if tile ~= nil then
            mouseEnterTile(player, true, tile.guid)
        end
    end
end

function mouseExitWhimCard(player, mouse, id)
    player = getPlayerFromColor(player.color, true)
    if player ~= nil and player.color == savedData.currentTurn then
        getObjectFromId(id).highlightOff(player.color)
        local tile = getTileFromWhimId(id)
        if tile ~= nil then
            mouseExitTile(player, true, tile.guid)
        end
    end
end

function getTileFromWhimId(id)
    local guid = getGuidFromId(id)
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if tile.card == guid then
                return tile
            end
        end
    end
    return nil
end

function refillDjinn()
    local offset = 7.5
    local djinnDeckObj = getObjectFromGUID(savedData.djinnDeck)
    local rot = djinnDeckObj.getRotation()
    rot.z = 0
    for d, djinn in ipairs(savedData.djinns) do
        Wait.frames(function()
            if refreshDjinn and djinn ~= '' and d ~= 4 then
                discardDjinn(getObjectFromGUID(djinn).guid)
                djinn = ''
            end
            if djinn == '' then
                local djinnObj = nil
                if d == 4 then
                    if expansionThieves and savedData.thievesDeck ~= nil then
                        local cardDraw = drawCard(savedData.thievesDeck)
                        djinnObj = cardDraw.cardObj
                        if cardDraw.deckObj ~= nil then
                            Wait.frames(function()
                                savedData.thievesDeck = cardDraw.deckObj.guid
                            end, 10)
                        else
                            savedData.thievesDeck = nil
                        end
                    else
                        return
                    end
                else
                    djinnObj = djinnDeckObj.takeObject()
                end
                djinnObj.setLock(true)
                djinnObj.interactable = false
                local tPos = getTransformPosition(djinnDeckObj, {x = offset*d, y = 0, z = 0})
                tPos.y = 1.03
                for i = 1, 5 do
                    Wait.frames(function()
                        djinnObj.setRotation(rot, false, true)
                    end, (i-1)*5)
                end
                djinnObj.setPositionSmooth(tPos, false, true)
                Wait.frames(function()
                    savedData.djinns[d] = djinnObj.guid
                    prepareDjinn(savedData.djinns[d])
                end, 10)
            end
        end, 5*d)
    end
end

function discardDjinn(djinn)
    local djinnDeckObj = getObjectFromGUID(savedData.djinnDeck)
    local djinnDeckPos = djinnDeckObj.getPosition()
    djinnDeckPos.y = -1
    local djinnDrawObj = getObjectFromGUID(djinn)
    djinnDrawObj.setPositionSmooth(djinnDeckPos, false, true)
    djinnDrawObj.UI.setXml("")
    Wait.time(function()
        --djinnDeckObj.setLock(false)
        --djinnDeckObj.putObject(djinnDrawObj)
        group({djinnDeckObj, djinnDrawObj})
        djinnDeckObj.randomize()
    end, gameSpeed*2)
end

function prepareDjinn(djinn)
    getObjectFromGUID(djinn).UI.setXmlTable({createButton(
        djinn, "0 0 -2",
        "0 0 0", "1 1 1", {360, 227}, {"", 0, ""},
        "", "", "clickDjinn", true, "highLightOn", "highLightOff", {"","0 0"}
    )})
end

function refillResources()
    local resourceDeckObj = getObjectFromGUID(savedData.resourceDeck)
    for i = 1, 9 - #savedData.resources do
        Wait.frames(function()
            local cardDraw = drawCard(savedData.resourceDeck)
            if cardDraw.deckObj ~= nil then
                Wait.frames(function()
                    savedData.resourceDeck = cardDraw.deckObj.guid
                    scaleDeck(getObjectFromGUID(savedData.resourceDeck))
                end, 4)
            else
                savedData.resourceDeck = savedData.resourceDiscard
                savedData.resourceDiscard = nil
                getObjectFromGUID(savedData.resourceDeck).randomize()
                getObjectFromGUID(savedData.resourceDeck).setPosition(savedData.resourceDeckPos)
                getObjectFromGUID(savedData.resourceDeck).setRotation(savedData.resourceDeckRot)
            end
            local resourceObj = cardDraw.cardObj
            resourceObj.setLock(true)
            resourceObj.interactable = false
            Wait.frames(function()
                table.insert(savedData.resources, resourceObj.guid)
                prepareResource(resourceObj.guid)
                arrangeResources()
                for j = 1, 5 do
                    local rot = resourceObj.getRotation()
                    Wait.frames(function()
                        resourceObj.setRotation(rot, false, true)
                    end, (j-1)*10)
                end
            end, 10)
        end, 5*i)
    end
	return 9 - #savedData.resources
end

function prepareResource(resource)
    getObjectFromGUID(resource).UI.setXmlTable({createButton(
        resource, "0 0 -2",
        "0 0 0", "1 1 1", {360, 231}, {"", 0, ""},
        "", "", "clickResource", true, "highLightOn", "highLightOff", {"","0 0"}
    )})
end

function arrangeResources(resourceCards)
    local offset = -4.2223
    local resourceDeckObj = getObjectFromGUID(savedData.resourceDeck)
    local rot = resourceDeckObj.getRotation()
    rot.z = 0
    local height = 3
    local offsetZ = 6
    if resourceCards == nil then
        resourceCards = savedData.resources
        height = 1.01
        offsetZ = 0
    end
    for r, resource in ipairs(resourceCards) do
        local resourceObj = getObjectFromGUID(resource)
        local tPos = getTransformPosition(resourceDeckObj, {x = offset*(r-1), y = 0, z = 0})
        tPos.y = height
        tPos.x = -tPos.x
        tPos.z = tPos.z + offsetZ
        resourceObj.setRotation(rot)
        resourceObj.setPositionSmooth(tPos, false, true)
    end
end

--Bidding Phase
biddingValues = {0,1,3,5,8,12,18}
function prepareTurnOrderTrack(turnTrack)
    local turnTrackXml = {}
    local offset = -2.2216*34
    local turnOrderTrackObj = getObjectFromGUID(turnTrack)
    table.insert(
        turnTrackXml, 
        createTextPanel("highlight", "200 0 -1", "0 0 180", 
            ".5 .5 .5", {600, 2000}, {"", 210},
            "", colors.solidGold, "", {"","0 0"}, false
        )
    )
    table.insert(turnTrackXml, 
        createText("banner", "230 -180 -200", "45 0 180", 
        "1 1 1", {5000, 5000}, {"BIDDING PHASE", 150}, "White", 
        "", {"Black","3 3"}, savedData.phase == 1)
    )
    for b, bid in ipairs(biddingValues) do
        table.insert(
            turnTrackXml, 
            createButton(
                turnTrack .. b, 230+offset*(b-1) .. " 25 -21",
                "0 0 0", "0.5 0.5 0.5", {400, 150}, {"", 0, ""},
                "", "", "clickTurnOrderTrack", true, "mouseEnterTurnTrack", "", {"","0 0"}
            )
        )
    end
    turnOrderTrackObj.UI.setXmlTable(turnTrackXml)
end

function mouseEnterTurnTrack(player, mouse, id)
    if savedData.hotseatEnabled then
        player = getPlayerFromColor(savedData.currentTurn)
    else
        player = getPlayerFromColor(player.color)
    end
    if player ~= nil 
        and player.color == savedData.currentTurn
        and savedData.currentMarker ~= nil then
        local bidId = tonumber(string.sub(id,7,7))
        if checkValidBid(bidId) then
            local previewOrder = insertTurnOrder(table.copy(savedData.turnOrder), bidId)
            arrangeTurnOrderTrack(previewOrder)
        end
    end
end

function insertTurnOrder(turnOrder, bid)
    local currentMarker = table.copy(savedData.currentMarker)
    currentMarker.bid = bid
    local index = 0
    for t, turn in ipairs(turnOrder) do
        if turn.bid > bid then
            index = t
            break
        end
    end
    if index ~= 0 then
        table.insert(turnOrder, index, currentMarker)
    else
        table.insert(turnOrder, currentMarker)
    end
    return turnOrder
end

function checkIsTurn(player, isTurn)
    if isTurn then
        return true
    else
        local broadcastString = ""
        if savedData.currentTurn ~= nil then
            local currentPlayerTurn = getPlayerFromColor(savedData.currentTurn)
            broadcastString = "It is " .. currentPlayerTurn.name .. "'s turn. "
        end
        broadcastString = broadcastString .. "Please wait for your turn."
        broadcastToPlayer(broadcastString, player.color)
    end
end

function clickTurnOrderTrack(player, isTurn, id)
    if savedData.currentMarker ~= nil and checkIsTurn(player, isTurn) then
        local bidId = tonumber(string.sub(id,7,7))
        if not checkValidBid(bidId, true) then
            return
        elseif countGold(player) < biddingValues[bidId] then
            broadcastToPlayer("You do not have enough GC to bid on this spot.", player.color)
        else
            savedData.turnOrder = insertTurnOrder(savedData.turnOrder, bidId)
            savedData.currentMarker = nil
            arrangeTurnOrderTrack(savedData.turnOrder)
            local bidAmount = 0
            if player.action.spentFakirs == nil then
                bidAmount = biddingValues[bidId]
            else
                if bidId - #player.action.spentFakirs < 1 then
                    bidAmount = 0
                else
                    bidAmount = biddingValues[bidId - #player.action.spentFakirs]
                end
                broadcastToAll(player.name .. " used the Djinn Kumarbi to reduce the cost of their bid by " .. #player.action.spentFakirs .. " spaces.", player.color)
                local kumarbi = checkDjinn("Kumarbi", player)
                getObjectFromGUID(kumarbi.guid).UI.setAttribute(kumarbi.guid .. "Btn", "color", "")
                discardResources(player.action.spentFakirs)
                arrangePlayerResources(player)
            end
            spendGoldCoins(player, bidAmount)
            broadcastToAll(player.name .. " bid " .. bidAmount .. " GC on the Turn Order Track.", player.color)
            player.action = nil
            hidePlayerUi(player)
            if #savedData.bidOrder == 0 then
                Wait.time(function()
                    getObjectFromGUID(savedData.turnTrack).UI.hide("bannerTxt")
                    savedData.phase = 2
                    setNextTurn()
                end, gameSpeed)
            else
                setNextTurn()
            end
        end
    end
end

function checkValidBid(bidId, click)
    local counts = {0,0,0,0}
    local pushedBids = {}
    for t, turn in ipairs(savedData.turnOrder) do
        if turn.bid == bidId then
            if bidId == 1 or (#savedData.players == 5 and bidId < 5) then
                counts[bidId] = counts[bidId] + 1
                table.insert(pushedBids, turn)
                local count = counts[bidId]
                if (bidId ~= 1 and count > 1) or (bidId == 1 and count > 2) then
                    return false
                end
            else
                return false
            end
        end
    end
    if click and #pushedBids > 0 then
        activateDjinn("Kali", nil, pushedBids)
    end
    return true
end

function arrangeTurnOrderTrack(turnOrder)
    local offsetX = -2.2216
    local offsetZ = -2.195
    local start = {x = 6.73, y = 1.63, z = 2.30}
    local biddingValue = {0,1,3,5,8,12,18}
    local turnOrderTrackObj = getObjectFromGUID(savedData.turnTrack)
    local sameCount = 0
    for m = #turnOrder, 1, -1 do
        local marker = turnOrder[m]
        local bidValue = marker.bid - 1
        local height = 0
        if savedData.currentMarker ~= nil 
            and savedData.currentMarker.guid == marker.guid then
            height = 1.5
        end
        if m < #turnOrder and turnOrder[m].bid == turnOrder[m+1].bid then
            sameCount = sameCount + 1
        else
            sameCount = 0
        end
        local tPos = getTransformPosition(
            turnOrderTrackObj, {
                x = start.x + offsetX*bidValue,
                y = start.y + height,
                z = start.z + offsetZ*sameCount
            }
        )
        getObjectFromGUID(marker.guid).setPositionSmooth(tPos, false, true)
    end
end

function arrangeBidTrack()
    local spots = {-3.17, -1.05, 1.07, 3.23}
    local pos = {y = 1.63, z = 0.09}
    if #savedData.players == 5 then
        spots = {-4.12, -2.04, 0.04, 2.15, 4.22}
        pos.z = 0.06
    end
    local bidOrderObj = getObjectFromGUID(savedData.bidTrack)
    for c, marker in ipairs(savedData.bidOrder) do
        pos.x = spots[c]
        local tPos = getTransformPosition(bidOrderObj, pos)
        local playerMarkerObj = getObjectFromGUID(marker.guid)
        playerMarkerObj.setPositionSmooth(tPos, false, true)
    end
end

--Action Phase
function clickDjinn(player, isTurn, id)
    local djinnObj = getObjectFromId(id)
    local djinnPlayer = getPlayerFromDjinn(djinnObj.guid)
    if checkIsTurn(player, isTurn) and player.stealDjinn ~= true then
        if djinnPlayer ~= nil and djinnPlayer.color == player.color then
            local djinnInfo = nil
            for d, djinn in ipairs(player.djinns) do
                if djinn.guid == djinnObj.guid then
                    djinnInfo = djinn
                    break
                end
            end
            if djinnInfo.active then
                if djinnInfo.used then
                    broadcastToPlayer("You can only use this Djinn once per round.", player.color)
                    return
                end
                local djinnCosts = checkDjinnCost(player, djinnInfo.cost)
                if (djinnInfo.ready == nil and savedData.phase ~= 2 and savedData.phase ~= 5)
                    or (djinnInfo.ready ~= nil and djinnInfo.ready ~= true) then
                    broadcastToPlayer("You cannot use this Djinn right now.", player.color)
                elseif djinnInfo.cost ~= nil and #djinnCosts == 0 then
                    broadcastToPlayer("You do not have enough Elders and/or Fakirs to use this Djinn.", player.color)
                else
                    local same = false 
                    if player.selectedDjinn ~= nil then
                        same = (player.selectedDjinn.guid == djinnObj.guid)
                        if djinnInfo.stealFunc == nil then
                            selectDjinn(player)
                        end
                    end
                    if not same then
                        selectDjinn(player, djinnInfo)
                        player.previousAction = player.action
                        if #djinnCosts > 1 then
                            showPlayerDjinnCosts(player, djinnInfo, djinnCosts)
                        else
                            player.action = {
                                cost = djinnCosts[1],
                                djinn = djinnInfo
                            }
                            if djinnInfo.func ~= nil and djinnInfo.func ~= '' then
                                hidePlayerUi(player)
                                _G[djinnInfo.func](player, player.action)
                            else
                                broadcastToPlayer("Function not found for Djinn " .. djinnInfo.name .. ".", player.color)
                            end
                        end
                    end
                end
            end
        elseif isDrawnDjinn(player, djinnObj.guid) then
            for d, djinn in ipairs(player.drawnDjinns) do
                if djinn ~= djinnObj.guid then
                    discardDjinn(getObjectFromGUID(djinn).guid)
                else
                    table.insert(player.djinns, getDjinnInfo(djinn))
                    Wait.frames(function()
                        arrangePlayerDjinns(player)
                    end, 10)
                end
            end
            player.drawnDjinns = nil
            player.busy = false
            if savedData.phase == 5 then
                endRound(player)
            end
        elseif player.action ~= nil then
            local action = player.action
            if djinnPlayer == nil and action.djinnFunc ~= nil then
                local same = false
                if djinnObj.guid == action.djinn then
                    same = true
                end
                if action.djinn ~= nil then
                    local actionDjinnObj = getObjectFromGUID(action.djinn)
                    local pos = actionDjinnObj.getPosition()
                    pos.y = 1.04
                    actionDjinnObj.setPositionSmooth(pos, false, true)
                    action.djinn = nil
                end
                if not same then
                    action.djinn = djinnObj.guid
                    if djinnObj.guid ~= savedData.djinnDeck then
                        local pos = djinnObj.getPosition()
                        pos.y = 3
                        djinnObj.setPositionSmooth(pos, false, true)
                    end
                end
                _G[action.djinnFunc](player, action)
            end
        end
    elseif djinnPlayer ~= nil then
        if savedData.hotseatEnabled then
            player = djinnPlayer
        end
        if djinnPlayer.color == player.color 
            and player.stealDjinn == true 
            and player.color ~= savedData.currentTurn then
            local djinnInfo = nil
            for d, djinn in ipairs(player.djinns) do
                if djinn.guid == djinnObj.guid then
                    djinnInfo = djinn
                    break
                end
            end
            if djinnInfo.stealName == nil then
                stealDjinn(player, nil, djinnObj.guid)
            end
        end
    elseif findStolenItem(djinnObj.guid, player) ~= nil then
        if player.waitForThief == 0 then
            stealDjinn(player, player.selectedDjinn, djinnObj.guid)
        else
            broadcastToPlayer("Wait for people to finish discarding the Djinn.", player.color)
        end
    end
end

function selectDjinn(player, djinn)
    local deselect = false
    if djinn == nil then
        djinn = player.selectedDjinn
        deselect = true
        if djinn == nil then
            return
        end
    end
    local djinnObj = getObjectFromGUID(djinn.guid)
    local pos = djinnObj.getPosition()
    if deselect == true then
        pos.y = 1.02
        player.selectedDjinn = nil
        if djinn.tileFunc ~= nil then
            resetTileColors()
        end
        if not player.busy then
            player.action = player.previousAction
            player.previousAction = nil
            if savedData.phase == 5 then
                endRound(player)
            else
                hidePlayerUi(player)
                showPlayerUI(player)
            end
        end
        djinnObj.UI.setAttribute(djinn.guid .. "Btn", "color", "")
        checkActiveDjinns(player)
        Wait.frames(function()
            djinnObj.setPositionSmooth(pos, false, true)
        end, 10)
    else
        pos.y = 3
        player.selectedDjinn = djinn
    end
    djinnObj.setPositionSmooth(pos, false, true)
end

function showPlayerDjinnCosts(player, djinn, costs)
    player.action = {
        passTxt = getCostString(costs[1]),
        passAltTxt = getCostString(costs[2]),
        passFunc = djinn.func,
        infoTxt = 'or',
        costs = costs,
        djinn = djinn
    }
    showPlayerUI(player)
end

function getCostString(cost, hidePay)
    local costString = ""
    if hidePay ~= true then
        costString = "Pay "
    end
    if cost[1] > 0 then
        costString = costString .. cost[1] .. " Elder"
        if cost[1] > 1 then
            costString = costString .. "s"
        end
    end
    if cost[2] > 0 then
        if cost[1] > 0 then
            costString = costString .. " and "
        end
        costString = costString .. cost[2] .. " Fakir"
        if cost[2] > 1 then
            costString = costString .. "s"
        end
    end
    return costString
end

function getPlayerFromDjinn(djinnGuid)
    for p, player in ipairs(savedData.players) do
        for d, djinn in ipairs(player.djinns) do
            if djinn.guid == djinnGuid then
                return player
            end
        end
    end
    return nil
end

function checkDjinnCost(player, costs)
    local elderNum = #getMeeples(player, "White Elder")
    local fakirNum = #getFakirs(player)
    local djinnCosts = {}
    for c, cost in ipairs(costs) do
        if cost[1] <= elderNum and cost[2] <= fakirNum then
            table.insert(djinnCosts, cost)
        end
    end
    return djinnCosts
end

function getPlayerFromResource(resource) 
    for p, player in ipairs(savedData.players) do
        for r, res in ipairs(player.resources) do
            if res == resource then
                return player
            end
        end
    end
end

function clickResource(player, isTurn, id)
    local resourceObj = getObjectFromId(id)
    local resourceGuid = getGuidFromId(id)
    local resourcePlayer = getPlayerFromResource(resourceGuid)
    local pos = resourceObj.getPosition()
    if checkIsTurn(player, isTurn) and player.stealResources ~= true then
        local action = player.action
        if player.selectedResources ~= nil then
            if isSelectedResource(player, resourceGuid) then
                for r, resource in ipairs(player.selectedResources) do
                    if resource == resourceGuid then
                        table.remove(player.selectedResources, r)
                        break
                    end
                end
                table.insert(savedData.resources, resourceGuid)
                arrangeResources()
                if #savedData.resources == 9 then
                    player.action = player.previousAction
                    player.selectedResources = nil
                    player.busy = false
                    resetResourceColors()
                    if player.action ~= nil and player.action.amount ~= nil then
                        highlightResourceRow(player.action.amount)
                        player.action.resources = {}
                    end
                    showPlayerUI(player)
                end
            elseif isResourceRow(resourceGuid) ~= 10 then
                local resNum = isResourceRow(resourceGuid)
                table.remove(savedData.resources, resNum)
                table.insert(player.selectedResources, 1, resourceGuid)
                arrangeResources(player.selectedResources)
                arrangeResources()
            end
        elseif savedData.phase == 5 
            and resourceObj.getName() ~= 'Fakir'
            and isPlayerResource(player, resourceObj.guid) then
            if action == nil 
                or (action.skipActions ~= true 
                    and action.resources == nil) then
                return
            end
            if action.resources == nil then
                player.previousAction = player.action
                player.action = {
                    passFunc = 'sellResources',
                    resources = {}
                }
                action = player.action
            end
            local found = false
            for r, resource in ipairs(action.resources) do
                if resource == resourceObj.guid then
                    table.remove(action.resources, r)
                    pos.y = 1.04
                    resourceObj.setPositionSmooth(pos, false, true)
                    found = true
                    if #action.resources == 0 then
                        player.action = player.previousAction
                        showPlayerUI(player)
                        return
                    end
                end
            end
            if not found then
                table.insert(action.resources, resourceObj.guid)
                pos.y = 3
                resourceObj.setPositionSmooth(pos, false, true)
            end
            local sellPrice = scoreResources(player, action.resources)
            player.action.passTxt = "Sell " .. #action.resources .. " Resource(s) for " .. sellPrice .. " GC"
            showPlayerUI(player)
        elseif action ~= nil then
            if action.spentFakirs ~= nil 
                and resourceObj.getName() == 'Fakir'
                and isPlayerResource(player, resourceGuid) then
                local found = false
                for f, fakir in ipairs(action.spentFakirs) do
                    if fakir == resourceGuid then
                        table.remove(action.spentFakirs, f)
                        table.insert(player.resources, resourceGuid)
                        pos.y = 1.04
                        found = true
                        break
                    end
                end
                if not found then
                    table.insert(action.spentFakirs, resourceGuid)
                    pos.y = 3
                    for r, resource in ipairs(player.resources) do
                        if resource == resourceGuid then
                            table.remove(player.resources, r)
                            break
                        end
                    end
                end
                resourceObj.setPositionSmooth(pos, false, true)
                if player.action.infoTxt2 ~= nil then
                    player.action.infoTxt2 = "Fakirs Spent : " .. #action.spentFakirs
                else
                    player.action.infoTxt = "Fakirs Spent : " .. #action.spentFakirs
                end
                showPlayerUI(player)
                if action.updateFunc ~= nil then
                    _G[action.updateFunc](player, action)
                end
            elseif action.resourceFunc ~= nil then
                local rNum = isResourceRow(resourceGuid)
                if rNum == 0 then
                    return
                elseif action.amount ~= nil and rNum > action.amount then
                    broadcastToPlayer("You can only buy one of the first " .. action.amount .. " Resource cards.", player.color)
                    return
                end
                for r, resource in ipairs(action.resources) do
                    if resource == resourceObj.guid then
                        table.remove(action.resources, r)
                        pos.y = 1.04
                        resourceObj.setPositionSmooth(pos, false, true)
                        return
                    end
                end
                table.insert(action.resources, resourceGuid)
                pos.y = 3
                resourceObj.setPositionSmooth(pos, false, true)
                if action.qty == #action.resources or #action.resources == #savedData.resources then
                    player.action = nil
                    _G[action.resourceFunc](player, table.copy(action))
                end
            end
        end
    elseif resourcePlayer ~= nil then
        if savedData.hotseatEnabled then
            player = resourcePlayer
        end
        if resourcePlayer.color == player.color 
            and player.stealResources == true
            and player.color ~= savedData.currentTurn then
            for r, resource in ipairs(player.selectedResources) do
                if resource == resourceGuid then
                    table.remove(player.selectedResources, r)
                    pos.y = 1.04
                    resourceObj.setPositionSmooth(pos, false, true)
                    return
                end
            end
            table.insert(player.selectedResources, resourceGuid)
            pos.y = 3
            resourceObj.setPositionSmooth(pos, false, true)
            if #player.selectedResources == 2 then
                stealResources(player, nil, player.selectedResources)
                player.selectedResources = nil
            end
        end
    elseif findStolenItem(resourceGuid, player) ~= nil then
        if player.waitForThief == 0 then
            for r, resource in ipairs(player.selectedResources) do
                if resource == resourceGuid then
                    table.remove(player.selectedResources, r)
                    pos.y = 3
                    resourceObj.setPositionSmooth(pos, false, true)
                    return
                end
            end
            table.insert(player.selectedResources, resourceGuid)
            pos.y = 5
            resourceObj.setPositionSmooth(pos, false, true)
            if #player.selectedResources == 2 then
                stealResources(player, player.selectedDjinn, player.selectedResources)
                player.selectedResources = nil
            end
        else
            broadcastToPlayer("Wait for people to finish discarding the Resources.", player.color)
        end
    end
end

function sellResources(player, action)
    local sellPrice = scoreResources(player, action.resources)
    takeGoldCoins(player, sellPrice)
    for _, resource in ipairs(action.resources) do
        for r, res in ipairs(player.resources) do
            if resource == res then
                table.remove(player.resources, r)
                break
            end
        end
    end
    discardResources(action.resources)
    arrangePlayerResources(player)
    endRound(player)
end

function isResourceRow(resource)
    for r, res in ipairs(savedData.resources) do
        if res == resource then
            return r
        end
    end
    return 10
end

function isSelectedResource(player, resource)
    for r, res in ipairs(player.selectedResources) do
        if res == resource then
            return true
        end
    end
    return false
end

function isPlayerResource(player, resourceGuid) 
    for r, resource in ipairs(player.resources) do
        if resource == resourceGuid then
            return true
        end
    end
    if player.action ~= nil and player.action.spentFakirs ~= nil then
        for f, fakir in ipairs(player.action.spentFakirs) do
            if fakir == resourceGuid then
                return true
            end
        end
    end
    return false
end

function colorTile(tile, color)
    getObjectFromGUID(tile.guid).UI.setAttribute(tile.guid .. "Btn", "color", color)
end

function clickTile(player, isTurn, id)
    local tile = getTileFromId(id)
    if (gameMode > 1 and savedData.phase == 1) 
        or (checkIsTurn(player, isTurn) 
            and player.stealCamel ~= true 
            and player.stealBuilding ~= true) then
        if player.hoveredTile ~= nil 
            and tile.guid == player.hoveredTile.guid 
            and countGuids(player.selectedMeeples) == 1 
            and #player.selectedTiles > 0
            and player.selectedTiles[1].guid ~= tile.guid then
            clickMeeple(player, true, getLastMeeple(player.selectedMeeples))
        elseif player.action ~= nil and player.action.selectedTiles ~= nil then
            for s, selectTile in ipairs(player.action.selectedTiles) do
                if selectTile.guid == tile.guid then
                    player.action.tile = selectTile
                    _G[player.action.tileFunc](player, player.action)
                    return
                end
            end
            broadcastToPlayer("You must select a valid Tile.", player.color)
        elseif savedData.phase == 2 and player.busy ~= true then
            if #player.selectedTiles == 0 then
                if #tile.meeples > 0 then
                    arrangeTileItems(tile, 2)
                    table.insert(player.selectedTiles, tile)
                    player.selectedMeeples = table.copy(tile.meeples)
                    tile.meeples = {}
                    getObjectFromGUID(tile.guid).UI.setAttribute(tile.guid .. "Btn", "text", "â†º")
                    getObjectFromGUID(tile.guid).UI.setAttribute(tile.guid .. "Btn", "textColor", "Black")
                end
            elseif player.selectedTiles[1].guid == tile.guid then
                for t, selTile in ipairs(player.selectedTiles) do
                    while #selTile.pendingMeeples > 0 do
                        table.insert(player.selectedMeeples, table.remove(selTile.pendingMeeples, 1))
                    end
                    arrangeTileItems(selTile)
                end
                getObjectFromGUID(tile.guid).UI.setAttribute(tile.guid .. "Btn", "text", "")
                player.selectedTiles = {}
                tile.meeples = {}
                while #player.selectedMeeples > 0 do
                    local guid = table.remove(player.selectedMeeples, 1)
                    if guid ~= '' then
                        table.insert(tile.meeples, guid)
                    end
                end
                arrangeTileItems(tile)
                player.selectedMeeples = {}
                player.hoveredTile = nil
            end
        elseif savedData.phase == 1 and isTurn then
            broadcastToPlayer("It is your turn to bid.", player.color)
        end
    elseif tile.camel ~= nil and findStolenItem(tile.guid, player) == nil then
        if savedData.hotseatEnabled then
            player = getPlayerFromColor(getCamelColor(tile.camel))
        end
        if getCamelColor(tile.camel) == player.color 
            and player.color ~= savedData.currentTurn then
            if player.stealCamel == true then
                stealCamel(player, nil, tile)
            elseif player.stealBuilding == true and #tile.buildings ~= 0 then
                stealBuilding(player, nil, tile)
            end
        end
    elseif findStolenItem(tile.guid, player) ~= nil then
        if player.waitForThief == 0 then
            if player.stealCamel == true then
                stealCamel(player, player.selectedDjinn, tile)
            elseif player.stealBuilding == true then
                stealBuilding(player, player.selectedDjinn, tile)
            end
        else
            broadcastToPlayer("Wait for people to finish selecting Tiles.", player.color)
        end
    end
end

moveFound = false
function findLegalMove(tile, selectedTiles, selectedMeeples, boardCopy, allMoves, ignore, meepleNum, ready)
    if moveFound then
        return
    end
    if selectedTiles == nil then
        if #tile.meeples == 0 then
            return {}
        end
        selectedMeeples = table.copy(tile.meeples)
        selectedTiles = {table.copy(tile)}
        allMoves = {}
        boardCopy = table.copy(savedData.board)
        boardCopy[tile.row][tile.col].meeples = {}
    else
        boardCopy = table.copy(boardCopy)
        if inBounds(tile[1], tile[2]) then
            tile = boardCopy[tile[1]][tile[2]]
            local prevTile = selectedTiles[#selectedTiles]
            if isBlockedByMountains(prevTile, tile) 
                or isBlockedByMountains(tile, prevTile) 
                or tile.points == nil then
                return
            end
        else
            return
        end
        table.insert(selectedTiles, tile)
        if #selectedMeeples == 1 then
            local selectedName = getObjectFromGUID(selectedMeeples[1]).getName()
            for m, meeple in ipairs(tile.meeples) do
                local meepleName = getObjectFromGUID(meeple).getName()
                if selectedName == meepleName then
                    table.insert(allMoves, {
                        selectedTiles = selectedTiles,
                        selectedMeeples = selectedMeeples
                    })
                    moveFound = true
                    return
                end
            end
            return
        else
            selectedMeeples = table.copy(selectedMeeples)
            table.insert(tile.meeples, table.remove(selectedMeeples, meepleNum))
        end
    end
    local directions = {
        {name = 'up', dir = {-1,0}, ignore = 'down'},
        {name = 'down', dir = {1,0}, ignore = 'up'},
        {name = 'left', dir = {0,-1}, ignore = 'right'},
        {name = 'right', dir = {0,1}, ignore = 'left'}
    }
    for i, d in ipairs(directions) do
        if ignore ~= d.name then
            local meepleNames = {}
            for m, meeple in ipairs(selectedMeeples) do
                local found = false
                for _, name in ipairs(meepleNames) do
                    if name == getObjectFromGUID(meeple).getName() then
                        found = true
                        break
                    end
                end
                if not found then
                    table.insert(meepleNames, getObjectFromGUID(meeple).getName())
                    findLegalMove(
                        {tile.row + d.dir[1], tile.col + d.dir[2]}, 
                        selectedTiles, 
                        selectedMeeples, 
                        boardCopy, 
                        allMoves, 
                        d.ignore, 
                        m
                    )
                end
            end
        end
    end
    return allMoves
end

function riseMeeple(meeple, rise)
    local meepleObj = getObjectFromGUID(meeple)
    local pos = meepleObj.getPosition()
    local tile = getTileFromId(meeple)
    if rise then
        pos.y = 2
    else
        if tile ~= nil then
            pos.y = 1.14
        else
            pos.y = 1
        end
    end
    meepleObj.setPositionSmooth(pos, false, true)
end

function clickMeeple(player, isTurn, id)
    if checkIsTurn(player, isTurn) then
        local tile = getTileFromId(id)
        local meepleGuid = getGuidFromId(id)
        local meeplePlayer = getPlayerFromMeeple(meepleGuid)
        local pendingTile = getPendingMeepleTile(id)
        if player.action ~= nil and player.action.meepleFunc ~= nil then
            if tile == nil and pendingTile == nil and player.action.item ~= nil then
                broadcastToPlayer("You can only select Meeples on Tiles.", player.color)
                return
            end
            if player.selectTwoMeeples == true then
                if #player.action.meeples == 0 then
                    table.insert(player.action.meeples, meepleGuid)
                    riseMeeple(meepleGuid, true)
                    if player.action.item ~= nil then
                        player.action.passFunc = 'removeMeeples'
                        player.action.passTxt = 'Remove Meeple'
                        player.action.tile = tile
                        showPlayerUI(player)
                    end
                    return
                else
                    local oldMeepleGuid = player.action.meeples[1]
                    local oldMeepleTile = getTileFromId(oldMeepleGuid)
                    local oldMeeplePlayer = getPlayerFromMeeple(oldMeepleGuid)
                    if oldMeepleGuid == meepleGuid then
                        player.action.meeples = {}
                        riseMeeple(meepleGuid, false)
                        if #player.action.meeples == 0 and player.action.item ~= nil then
                            hidePlayerUi(player)
                        end
                        return
                    elseif (oldMeepleTile ~= nil and oldMeepleTile.guid ~= tile.guid)
                        or (oldMeeplePlayer ~= nil and oldMeeplePlayer.color ~= meeplePlayer.color) then
                        broadcastToPlayer("You must select two Meeples from the same Tile/player.", player.color)
                        return
                    end 
                end
            end
            if player.action.meepleSendOne == true then
                _G[player.action.meepleFunc](player, meepleGuid)
            else
                table.insert(player.action.meeples, meepleGuid)
                player.selectTwoMeeples = nil
                _G[player.action.meepleFunc](player, player.action)
            end
        elseif tile ~= nil and savedData.phase == 2 then
            if #player.selectedTiles > 0 
                and isSelectedMeeple(player, meepleGuid) then
                local hoverTile = player.hoveredTile
                if hoverTile ~= nil then
                    local lastMeeple = getObjectFromGUID(getLastMeeple(player.selectedMeeples)).getName()
                    if countGuids(player.selectedMeeples) == 1 
                        and not sameMeepleOnTile(player.selectedMeeples, hoverTile) then
                        broadcastToPlayer("Cannot end movement on a Tile without a matching Meeple color.", player.color)
                        return
                    end
                    table.insert(player.selectedTiles, hoverTile)
                    table.insert(hoverTile.pendingMeeples, removeMeeple(player.selectedMeeples, meepleGuid, true))
                    arrangeTileItems(hoverTile)
                    if countGuids(player.selectedMeeples) == 0 then
                        for t, tile in ipairs(player.selectedTiles) do
                            while #tile.pendingMeeples > 0 do
                                table.insert(tile.meeples, table.remove(tile.pendingMeeples, 1))
                            end
                            arrangeTileItems(tile)
                            if tile.camel ~= nil then
                                activateDjinn("Marid", getCamelPlayer(tile.camel))
                            end
                        end
                        local firstTile = player.selectedTiles[1]
                        getObjectFromGUID(firstTile.guid).UI.setAttribute(firstTile.guid .. "Btn", "text", "")
                        savedData.phase = 3
                        player.selectedTiles = {}
                        arrangeTileItems(firstTile)
                        player.selectedMeeples = {}
                        Wait.time(function()
                            if player.flying == true then
                                player.flying = false
                                useAndDiscardItem(player, player.action.item, false)
                            end
                            resolveTribeAction(player, lastMeeple, hoverTile)
                        end, gameSpeed)
                    end
                    player.hoveredTile = nil
                else
                    local currentTile = player.selectedTiles[#player.selectedTiles]
                    if #currentTile.pendingMeeples > 0 then
                        local lastMeeple = table.remove(currentTile.pendingMeeples, #currentTile.pendingMeeples)
                        table.insert(currentTile.pendingMeeples, meepleGuid)
                        arrangeTileItems(currentTile)
                        removeMeeple(player.selectedMeeples, meepleGuid, true)
                        for m, meeple in ipairs(player.selectedMeeples) do
                            if meeple == '' then
                                player.selectedMeeples[m] = lastMeeple
                                arrangeTileItems(currentTile, 2, player.selectedMeeples)
                                return
                            end
                        end
                    end
                end
            else
                clickTile(player, isTurn, tile.guid)
            end
        elseif savedData.phase == 1 and isTurn then
            broadcastToPlayer("It is your turn to bid.", player.color)
        elseif pendingTile ~= nil then
            local undoMeeple = getGuidFromId(id)
            for i = #player.selectedTiles, 1, -1 do
                local tile = table.remove(player.selectedTiles, i)
                local tileMeeple = table.remove(tile.pendingMeeples, #tile.pendingMeeples)
                for m, meeple in ipairs(player.selectedMeeples) do
                    if meeple == '' then
                        player.selectedMeeples[m] = tileMeeple
                        break
                    end
                end
                arrangeTileItems(tile)
                if undoMeeple == tileMeeple then
                    arrangeTileItems(tile, 2, player.selectedMeeples)
                    player.hoverTile = tile
                    return
                end
            end
        end
    end
end

function getCamelPlayer(camelGuid)
    local color = getCamelColor(camelGuid)
    return getPlayerFromColor(color)
end

function getCamelColor(guid)
    local camelColor = string.split(getObjectFromGUID(guid).getName(), ' ')[1]
    if camelColor == "Black" then
        camelColor = "White"
    end
    return camelColor
end

function controlTile(player, tile)
    if tile.camel == nil and #player.camels > 0 then
        local action = {
            passFunc = 'takeTileControl',
            passTxt = 'Place Camel',
            passAltTxt = 'Place Tent',
            infoTxt = 'or',
            tile = tile,
        }
        if getTent(player.camels) == nil then
            takeTileControl(player, action)
        else
            broadcastToPlayer("Choose to place a Camel or Tent on the " .. tile.name .. " Tile for control.", player.color)
            colorTile(tile, getTransparentColor(player.color))
            player.busy = true
            player.action = action
            showPlayerUI(player)
        end
    end
end

function getTent(camels, takeOut)
    for c, camel in ipairs(camels) do
        if string.match(getObjectFromGUID(camel).getName(), "Tent") then
            if takeOut == true then
                return table.remove(camels, c)
            else
                return camel
            end
        end
    end
    return nil
end

function takeTileControl(player, action, useTent)
    local tile = action.tile
    local camel = nil
    if useTent then
        camel = getTent(player.camels, true)
    else
        camel = table.remove(player.camels, 1)
    end
    tile.camel = camel
    placeCamel(tile, 3)
    Wait.time(function()
        placeCamel(tile)
        resetTileColors()
    end, gameSpeed)
    arrangeCamels(player)
    broadcastToAll(player.name .. " took control of the " .. tile.points .. " point ".. tile.name .. " Tile.", player.color)
    player.busy = false
    if #player.camels == 0 and not savedData.endGame then
        savedData.endGame = true
        broadcastToAll(player.name .. " placed thier last Camel.", player.color)
        broadcastToAll("END OF GAME TRIGGERED!", messageColor)
    end
    player.action = player.previousAction
    checkPlayerWhims(player)
    hidePlayerUi(player)
    showPlayerUI(player)
end

function placeCamel(tile, height)
    if height == nil then
        height = 0
    end
    local tileObj = getObjectFromGUID(tile.guid)
    local camelObj = getObjectFromGUID(tile.camel)
    local pos = {x = -2.20, y = 0.14, z = 2.20}
    if string.match(camelObj.getName(), 'Tent') then
        pos = {x = -2, y = 0.14, z = 2}
    end
    pos.y = pos.y + height
    local rot = tileObj.getRotation()
    rot.y = 225
    local tPos = getTransformPosition(tileObj, pos)
    camelObj.setRotationSmooth(rot, false, true)
    camelObj.setPositionSmooth(tPos, false, true)
    if highlightMeeple then
        camelObj.highlightOn("Black")
    end
end

function countGuids(guids)
    local count = 0
    for g, guid in ipairs(guids) do
        if guid ~= '' then
            count = count + 1
        end
    end
    return count
end

function getLastMeeple(meeples)
    for m, meeple in ipairs(meeples) do
        if meeple ~= '' then
            return meeple
        end
    end
end

function sameMeepleOnTile(selectedMeeples, tile)
    local meepleName = getObjectFromGUID(getLastMeeple(selectedMeeples)).getName()
    for m, meeple in ipairs(tile.meeples) do
        if meepleName == getObjectFromGUID(meeple).getName() then
            return true
        end
    end
    for m, meeple in ipairs(tile.pendingMeeples) do
        if meepleName == getObjectFromGUID(meeple).getName() then
            return true
        end
    end
    return false
end

function removeMeeple(meeples, guid, blank)
    for m, meeple in ipairs(meeples) do
        if meeple == guid then
            local meepleGuid = getObjectFromGUID(meeple).guid
            if blank == true then
                meeples[m] = ''
            else
                table.remove(meeples, m)
            end
            return meepleGuid
        end
    end
end

function isSelectedMeeple(player, guid)
    for m, meeple in ipairs(player.selectedMeeples) do
        if meeple == guid then
            return true
        end
    end
    return false
end

function isAdjacent(tile, player)
    local prevTile = player.selectedTiles[#player.selectedTiles]
    if #player.selectedTiles > 1
        and player.selectedTiles[#player.selectedTiles-1].guid == tile.guid 
        and player.flying ~= true then
        return false
    end
    local rowNum = math.abs(tile.row - prevTile.row)
    local colNum = math.abs(tile.col - prevTile.col)
    if ((rowNum == 1 and colNum == 0)
            or (rowNum == 0 and colNum == 1)
            or (player.flying == true 
                and countGuids(player.selectedMeeples) == 1
                and (rowNum > 0 or colNum > 0)))
        and (not isBlockedByMountains(prevTile, tile) 
            or player.flying == true) then
        return true
    else
        return false
    end
end

function isBlockedByMountains(prevTile, tile)
    local rowNum = tile.row - prevTile.row
    local colNum = tile.col - prevTile.col
    if (rowNum == 1 and (tile.mountainUp ~= nil or prevTile.mountainDown ~= nil))
        or (rowNum == -1 and (tile.mountainDown ~= nil or prevTile.mountainUp ~= nil))
        or (colNum == 1 and (tile.mountainLeft ~= nil or prevTile.mountainRight ~= nil))
        or (colNum == -1 and (tile.mountainRight ~= nil or prevTile.mountainLeft ~= nil)) then
        return true
    else
        return false
    end
end

function inBounds(row, col)
    if col > 0 and col < 7 and row > 0 and row < #savedData.board+1  then
        return true
    else
        return false
    end
end

function getTileFromId(id)
    local guid = getGuidFromId(id)
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if tile.guid == guid then
                return tile
            end
            for m, meeple in ipairs(tile.meeples) do
                if meeple == guid then
                    return tile
                end
            end
        end
    end
    local player = getPlayerFromColor(savedData.currentTurn)
    if player ~= nil then
        for m, meeple in ipairs(player.selectedMeeples) do
            if meeple == guid then
                return player.selectedTiles[1]
            end
        end
    end
end

function getPendingMeepleTile(id)
    local guid = getGuidFromId(id)
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            for m, meeple in ipairs(tile.pendingMeeples) do
                if meeple == guid then
                    return tile
                end
            end
        end
    end
end

function mouseEnterTile(player, mouse, id)
    player = getPlayerFromColor(player.color, true)
    if player ~= nil and player.color == savedData.currentTurn then
        if #player.selectedTiles > 0 then
            local tile = getTileFromId(id)
            getObjectFromGUID(tile.guid).UI.setAttribute(tile.guid .. "Btn", "textColor", "White")
            if tile.points ~= nil and isAdjacent(tile, player) then
                arrangeTileItems(tile, 2, player.selectedMeeples)
                player.hoveredTile = tile
            end
        end
    end
end

function mouseExitTile(player, mouse, id)
    player = getPlayerFromColor(player.color, true)
    if player ~= nil and player.color == savedData.currentTurn then
        local tile = getTileFromId(id)
        getObjectFromGUID(tile.guid).UI.setAttribute(tile.guid .. "Btn", "textColor", "Black")
    end
end

function takeGoldCoins(player, amount)
    player.goldTotal = player.goldTotal + amount
    updateScore()
    local count = 0
    while amount > 0 do
        local bagObj = getObjectFromGUID(oneGoldBagGuid)
        if amount > 5 then
            amount = amount - 5
            bagObj = getObjectFromGUID(fiveGoldBagGuid)
        else
            amount = amount - 1
        end
        count = count + 1
        Wait.time(function()
            local coinObj = bagObj.takeObject()
            coinObj.setLock(true)
            coinObj.interactable = false
            Wait.frames(function()
                table.insert(player.coins, coinObj.guid)
                arrangeGoldCoins(player)
                countGold(player)
            end, 5)
        end, gameSpeed/7*count)
    end
end

function spendGoldCoins(player, amount)
    player.goldTotal = player.goldTotal - amount
    updateScore()
    local count = 0
    local oneBagObj = getObjectFromGUID(oneGoldBagGuid)
    local fiveBagObj = getObjectFromGUID(fiveGoldBagGuid)
    for c = #player.coins, 1, -1 do
        local coinObj = getObjectFromGUID(player.coins[c])
        if amount >= 5 and string.match(coinObj.getName(), 5) then
            amount = amount - 5
            count = count + 1
            Wait.time(function()
                table.remove(player.coins, c)
                fiveBagObj.putObject(coinObj)
                arrangeGoldCoins(player)
                countGold(player)
            end, gameSpeed/5*count)
        elseif amount > 0 and string.match(coinObj.getName(), 1) then
            amount = amount - 1
            count = count + 1
            Wait.time(function()
                table.remove(player.coins, c)
                oneBagObj.putObject(coinObj)
                arrangeGoldCoins(player)
                countGold(player)
            end, gameSpeed/5*count)
        end
    end
    if amount > 0 then
        amount = amount - 5
        count = count + 1
        Wait.time(function()
            local fiveCoinObj = getObjectFromGUID(table.remove(player.coins, 1))
            fiveBagObj.putObject(fiveCoinObj)
            while amount < 0 do
                amount = amount + 1
                count = count + 1
                Wait.time(function()
                    local coinObj = oneBagObj.takeObject()
                    coinObj.setLock(true)
                    coinObj.interactable = false
                    Wait.frames(function()
                        table.insert(player.coins, coinObj.guid)
                        arrangeGoldCoins(player)
                        countGold(player)
                    end, 10)
                end, gameSpeed/5*count)
            end
        end, gameSpeed/5*count)
    end
end

function countGold(player)
    if savedData.manualMode then
        return 0
    end
    local total = 0
    for c, coin in ipairs(player.coins) do
        total = total + tonumber(string.split(getObjectFromGUID(coin).getName(), ' ')[1])
    end
    getObjectFromGUID(player.board).UI.setAttribute("goldTxt", "text", total)
    setVisiblity(getObjectFromGUID(player.board), "goldTxt", player.color, hiddenInformationOption == 3)
    return total
end

function arrangeGoldCoins(player)
    local boardObj = getObjectFromGUID(player.board)
    local rot = {x = 0, y = boardObj.getRotation().y, z = 180}
    local start = {x = 0, y = 0.22, z = 12.5}
    local height = 0
    for i, coin in ipairs(player.coins) do
        local obj = getObjectFromGUID(coin)
        local pos = getTransformPosition(boardObj, {z = start.z, x = start.x, y = start.y + 0.17*height})
        if hiddenInformationOption == 2 then
            obj.setInvisibleTo(getPlayerList(player.color))
        end
        obj.setRotation(rot)
        obj.setPositionSmooth(pos, false, true)
        if i%5 == 0 then
            height = 0
            if (i - 5)%10 == 0 then
                if ((i - 5)/10)%2 == 0 then
                    start.z = start.z - 0.25
                else
                    start.z = start.z + 0.25
                end
            end
            if start.x >= 0 then
                start.x = start.x + 2.25
            end
            start.x = start.x * (-1)
        else
            height = height + 1
        end
    end
end

function arrangeTileItems(tile, height, meeples)
    if height == nil then
        height = 0
    end
    if meeples == nil then
        meeples = tile.meeples
    end
    if meeples ~= nil then
        local meeplePos = {
            {x = 0, z = -1},
            {x = 1.5, z = 0},
            {x = -1.5, z = 0},
            {x = 0, z = 1},
            {x = 0, z = 0},
            {x = 1.5, z = -1},
            {x = -1.5, z = -1},
            {x = 1.5, z = 1},
            {x = 0, z = 2},
            {x = 1.5, z = 2}
        }
        if tile.card ~= nil and height == 0 then
            meeplePos = {
                {x = -1.5, z = 0},
                {x = -1.5, z = -1},
                {x = 0, z = 1},
                {x = 1.5, z = 1},
                {x = 0, z = 0},
                {x = 0, z = -1},
                {x = 1.5, z = 0},
                {x = 1.5, z = -1},
                {x = 0, z = 2},
                {x = 1.5, z = 2}
            }
        end
        local minus = 0
        local stack = 0
        for m, meeple in ipairs(meeples) do
            if m > #meeplePos then
                minus = #meeplePos
                stack = 1.5
            end
            if meeple ~= '' then
                placeMeepleOntile(meeple, tile, meeplePos[m-minus], height+stack)
            end
        end
        for m, meeple in ipairs(tile.pendingMeeples) do
            placeMeepleOntile(meeple, tile, meeplePos[#meeplePos-(m-1)], 0)
        end
    end
    local buildingPos = {
        {x = 1.75, z = -2.75, y = 0},
        {x = 0.7, z = -1.95, y = 0},
        {x = 2.75, z = -0.75, y = 270},
        {x = -2.75, z = 0, y = 90},
        {x = 0, z = 2.75, y = 180},
        {x = -1.25, z = -2.75, y = 180}
    }
    local tileObj = getObjectFromGUID(tile.guid)
    minus = 0
    stack = 0
    local rot = tileObj.getRotation()
    for b, building in ipairs(tile.buildings) do
        if b > #buildingPos then
            minus = #buildingPos
            stack = 3
        end
        local buildingObj = getObjectFromGUID(building)
        local pos = buildingPos[b-minus]
        rot.y = buildingPos[b-minus].y
        pos.y = 0.14+stack
        local tPos = getTransformPosition(tileObj, pos)
        buildingObj.setRotationSmooth(rot, false, true)
        buildingObj.setPositionSmooth(tPos, false, true)
    end
end

function placeMeepleOntile(meeple, tile, pos, height)
    local tileObj = getObjectFromGUID(tile.guid)
    local meepleObj = getObjectFromGUID(meeple)
    if tile.card == nil then
        pos.y = 0.15
    else
        pos.y = 0.18
    end
    pos.y = pos.y + height
    local tPos = getTransformPosition(tileObj, pos)
    meepleObj.setPositionSmooth(tPos, false, true)
end

function arrangeCamels(player)
    arrangeObjs(player.camels, player.board, -8, 1.5, 0.04, -90)
end

function arrangeObjs(objs, board, posZ, offset, height, rotY, rotZ, player)
    local boardObj = getObjectFromGUID(board)
    if rotY == nil then
        rotY = 0
    end
    if rotZ == nil then
        rotZ = 0
    end
    local rot = {x = 0, y = boardObj.getRotation().y + rotY, z = 0 + rotZ}
    local start = {x = 0, y = height, z = posZ}
    start.x = start.x - offset*((#objs+1)/2)
    for i, o in ipairs(objs) do
        local guid = o.guid
        if guid == nil then
            guid = o
        end
        local obj = getObjectFromGUID(guid)
        local pos = getTransformPosition(boardObj, {z = start.z, x = start.x+offset*i, y = start.y})
        obj.setRotation(rot)
        obj.setPositionSmooth(pos, false, true)
        if o.clone ~= nil then
            local cloneObj = getObjectFromGUID(o.clone)
            cloneObj.setRotation(rot)
            pos.y = pos.y - 0.005
            cloneObj.setPositionSmooth(pos, false, true)
            cloneObj.setInvisibleTo({player.color})
            obj.setInvisibleTo(getPlayerList(player.color))
        end
    end
end

function drawCard(deck)
    local deckObj = getObjectFromGUID(deck)
    local cardObj = deckObj.takeObject()
    if deckObj.remainder ~= nil then
        deckObj = deckObj.remainder
        deckObj.setLock(true)
        deckObj.interactable = false
    end
    if cardObj == nil then
        return {cardObj = deckObj, deckObj = nil}
    end
    cardObj.setLock(true)
    cardObj.interactable = false
    return {cardObj = cardObj, deckObj = deckObj}
end

function highLightOn(player, mouse, id)
    getHighlightType(player, id, true)
end

function highLightOff(player, mouse, id)
    getHighlightType(player, id, false)
end

function getHighlightType(player, id, on)
    player = getPlayerFromColor(player.color, true)
    if player ~= nil then
        local obj = getObjectFromId(id)
        local pos = obj.getPosition()
        if on then
            obj.highlightOn(player.color)
        else
            obj.highlightOff(player.color)
            if highlightMeeple and isMeeple(obj) 
                and (getTileFromId(obj.guid) ~= nil 
                    or getPendingMeepleTile(obj.guid) ~= nil) then
                obj.highlightOn("Black")
            end
        end
    end
end

function isMeeple(obj)
    for t, tribe in ipairs(tribes) do
        if tribe.name == obj.getName() .. "s" then
            return true
        end
    end
    return false
end

--Tribe Actions
function resolveTribeAction(player, meepleName, tile)
    local tribeMeeples = {}
    for i = #tile.meeples, 1, -1 do
        local meepleObj = getObjectFromGUID(tile.meeples[i])
        if meepleObj.getName() == meepleName then
            table.insert(tribeMeeples, table.remove(tile.meeples, i))
            local pos = meepleObj.getPosition()
            pos.y = pos.y + 2
            meepleObj.setPositionSmooth(pos, false, true)
        end
    end
    if #tile.meeples == 0 then
        controlTile(player, tile)
    end
    waitForTentPlacement(player, meepleName, tribeMeeples, tile)
    activateThieves(player, meepleName)
    resolveTileAction(player, tile)
end

function waitForTentPlacement(player, meepleName, tribeMeeples, tile)
    if player.busy == true then
        Wait.frames(function()
            waitForTentPlacement(player, meepleName, tribeMeeples, tile)
        end, 30)
    else
        for t, tribe in ipairs(tribes) do
            if string.match(tribe.name, meepleName) then
                Wait.time(function()
                    arrangeTileItems(tile)
                    _G[tribe.func](player, tribeMeeples, tile)
                end, gameSpeed)
                return
            end
        end
    end
end

function collectMeeple(player, tribeMeeples, noBroadcast)
    local name = getObjectFromGUID(tribeMeeples[1]).getName()
    if noBroadcast ~= true then
        broadcastToAll(player.name .. " collected " .. #tribeMeeples .. " " .. name .. "(s).", player.color)
    end
    for m, meeple in ipairs(tribeMeeples) do
        table.insert(player.meeples, meeple)
        getObjectFromGUID(meeple).highlightOff()
        arrangePlayerMeeples(player)
    end
    if name == 'Yellow Vizier' then
        activateDjinn("Dhenim")
    end
    checkPlayerWhims(player)
    savedData.phase = 4
end

function arrangePlayerMeeples(player)
    local sortedMeeples = {}
    local rows = {
        {posZ = -10, size = 11},
        {posZ = -11.25, size = 8},
        {posZ = -12.5, size = 100}
    }
    if gameMode == 1 or player.color ~= "Blue" then
        local colorOrder = {'Yellow', 'White', 'Purple'}
        for c, color in ipairs(colorOrder) do
            for m, meeple in ipairs(player.meeples) do
                if string.match(getObjectFromGUID(meeple).getName(), color) then
                    table.insert(sortedMeeples, meeple)
                end
            end
        end
        player.meeples = sortedMeeples
        local meeplesCopy = table.copy(player.meeples)
        for r, row in ipairs(rows) do
            arrangeObjs(table.sub(meeplesCopy, row.size), player.board, row.posZ, 1.5, 0.04)
        end
    else
        local meepleGroups = {
            getMeeples(player, "Yellow Vizier"),
            getMeeples(player, "Red Assassin"),
            getMeeples(player, "White Elder")
        }
        for g, group in ipairs(meepleGroups) do
            arrangeObjs(group, player.board, rows[g].posZ, 1.5, 0.04)
        end
    end
end

function takeResources(player, tribeMeeples, yazid)
    local name = getObjectFromGUID(tribeMeeples[1]).getName()
    broadcastToAll(player.name .. " drew Resource cards from the start of the line for " .. #tribeMeeples .. " " .. name .. "(s).", player.color)
    for i = 1, #tribeMeeples do
        Wait.time(function()
            if #savedData.resources > 0 then
                local resource = table.remove(savedData.resources, 1)
                arrangeResources()
                if yazid == true and getObjectFromGUID(resource).getName() ~= 'Fakir' then
                    discardResources({resource})
                else
                    table.insert(player.resources, resource)
                    arrangePlayerResources(player)
                end
            end
        end, gameSpeed/4*i)
    end
    Wait.time(function()
        returnMeeples(tribeMeeples)
        savedData.phase = 4
        Wait.frames(function()
            if yazid == true then
                player.busy = false
            end
        end, 10)
    end, gameSpeed/4*(#tribeMeeples + 1))
end

function sortResourcesBySet(resourceSet)
    local sortedResources = {}
    local found = true
    while found do
        found = false
        for r, resourceName in ipairs(resources) do
            for i, resource in ipairs(resourceSet) do
                local resourceObj = getObjectFromGUID(resource)
                if resourceObj.getName() == resourceName then
                    table.insert(sortedResources, table.remove(resourceSet, i))
                    found = true
                    break
                end
            end
        end
        if not found then
            for r, resource in ipairs(resourceSet) do
                table.insert(sortedResources, resource)
            end
        end
    end
    return sortedResources
end

function sortResourcesByType(resourceSet)
    local sortedResources = {}
    for r, resourceName in ipairs(resources) do
        for i, resource in ipairs(resourceSet) do
            local resourceObj = getObjectFromGUID(resource)
            if resourceObj.getName() == resourceName then
                table.insert(sortedResources, resource)
            end
        end
    end
    for r, resource in ipairs(resourceSet) do
        local resourceObj = getObjectFromGUID(resource)
        if resourceObj.getName() == 'Fakir' then
            table.insert(sortedResources, resource)
        end
    end
    return sortedResources
end

function arrangePlayerResources(player)
    player.resources = sortResourcesByType(player.resources)
    local i = 1
    local boardObj = getObjectFromGUID(player.board)
    local rot = {x = 0, y = boardObj.getRotation().y, z = 0}
    local start = {x = 0, y = 0.08, z = 4.5}
    local offset = -4.5
    local startX = start.x - offset*((#player.resources+1)/2)
    while startX+offset > 13.5 do
        offset = offset + 0.1
        rot.z = -1
        startX = start.x - offset*((#player.resources+1)/2)
    end
    for r, resource in ipairs(player.resources) do
        local obj = getObjectFromGUID(resource)
        local pos = getTransformPosition(boardObj, {z = start.z, x = startX+offset*r, y = start.y})
        obj.setRotation(rot)
        obj.setPositionSmooth(pos, false, true)
        if hiddenInformationOption == 1 then
            obj.setHiddenFrom(getPlayerList(player.color))
            setVisiblity(obj, resource .. "Btn", player.color, hiddenInformationOption == 3)
        elseif hiddenInformationOption == 2 then
            obj.setInvisibleTo(getPlayerList(player.color))
        end
        obj.UI.setAttribute(resource .. "Btn", "color", "")
    end
end

function scoreAdjacentBlueTiles(player, tribeMeeples, tile, kandicha)
    local blueTiles = getAdjacentColorTiles(tile, "Blue", colors.blue)
    removeMeepleButtons(tribeMeeples)
    local action = {
        name = 'blueTribe',
        blueTiles = blueTiles,
        tribeMeeples = tribeMeeples,
        spentFakirs = {},
        passFunc = 'resolveBlueAction',
        passTxt = 'Use Builder Action',
        infoTxt = 'Fakirs Spent : 0'
    }
    if kandicha == nil and (djinnReadyUp("Echidna", player) or #getFakirs(player) > 0) then
        broadcastToPlayer("Select any Fakirs you would like to spend, then click '" .. action.passTxt .. "'.", player.color)
        player.action = action
        showPlayerUI(player)
    else
        Wait.time(function()
            resolveBlueAction(player, action, nil, kandicha)
        end, gameSpeed)
    end
end

function getAdjacentColorTiles(tile, color, highlight)
    local checkDirs = {
        {-1,-1},{-1,0},{-1,1},
        {0,-1}, {0,0} ,{0,1},
        {1,-1}, {1,0} ,{1,1}
    }
    local colorTiles = {}
    for d, dir in ipairs(checkDirs) do
        if inBounds(tile.row + dir[1], tile.col + dir[2]) then
            local adjTile = savedData.board[tile.row + dir[1]][tile.col + dir[2]]
            if adjTile.color == color then
                table.insert(colorTiles, adjTile)
                if highlight ~= nil then
                    getObjectFromGUID(adjTile.guid).UI.setAttribute(adjTile.guid .. "Btn", "color", colors.blue)
                end
            end
        end
    end
    return colorTiles
end

function resolveBlueAction(player, var, alt, kandicha)
    local total = #var.blueTiles * (#var.tribeMeeples + #var.spentFakirs)
    if player.blueMult ~= nil then
        total = total*player.blueMult
        player.blueMult = nil
    end
    djinnReadyUp("Echidna", player, true)
    local broadcastString = player.name .. " gained " .. total .. " GC from " .. #var.tribeMeeples .. " Blue Builders"
    if #var.spentFakirs > 0 then
        broadcastString = broadcastString .. " plus " .. #var.spentFakirs .. " Fakir(s)"
        arrangePlayerResources(player)
        discardResources(var.spentFakirs)
    end
    broadcastString = broadcastString .. " times " .. #var.blueTiles .. " Blue valued Tile(s)."
    broadcastToAll(broadcastString, player.color)
    takeGoldCoins(player, total)
    activateWhim("Builder Action", player, total)
    for t, tile in ipairs(var.blueTiles) do
        colorTile(tile, '')
    end
    if kandicha == nil then
        returnMeeples(var.tribeMeeples)
        savedData.phase = 4
    end
end

function removeMeepleButtons(meeples)
    for m, meeple in ipairs(meeples) do
        getObjectFromGUID(meeple).UI.hide(meeple .. "Btn")
    end
end

function getFakirs(player)
    local fakirs = {}
    for r, resource in ipairs(player.resources) do
        if getObjectFromGUID(resource).getName() == 'Fakir' then
            table.insert(fakirs, resource)
        end
    end
    return fakirs
end

function assassinateMeeple(player, tribeMeeples, tile)
    broadcastToPlayer("Select a Meeple in range, or in front of another player to kill.", player.color)
    removeMeepleButtons(tribeMeeples)
    local infoTxt = 'Choose a Meeple to Kill'
    local infoTxt2 = nil
    if #getFakirs(player) > 0 then
        infoTxt = 'Choose a Meeple to Kill'
        infoTxt2 = 'Fakirs Spent : 0'
    end
    player.action = {
        tribeMeeples = tribeMeeples,
        tile = tile,
        spentFakirs = {},
        updateFunc = 'highlightRedArea',
        meeples = {},
        meepleFunc = 'resolveAssasination',
        infoTxt = infoTxt,
        infoTxt2 = infoTxt2
    }
    highlightRedArea(player, player.action)
    djinnReadyUp("Iblis", player)
    showPlayerUI(player)
end

function resolveAssasination(player, action)
    local tile = nil
    for m, meeple in ipairs(action.meeples) do
        tile = getTileFromId(meeple)
        local targetPlayer = getPlayerFromMeeple(meeple)
        local action = player.action
        local broadcastString = player.name .. " killed " 
        if tile ~= nil then
            local amount = #action.spentFakirs + #action.tribeMeeples
            local distance = math.abs(action.tile.row - tile.row) + math.abs(action.tile.col - tile.col)
            if distance > amount then
                broadcastToPlayer("This Meeple is not in range of the Red Assassins.", player.color)
                unselectMeeples(action.meeples)
                return
            end
            removeMeeple(tile.meeples, meeple)
            arrangeTileItems(tile)
            broadcastString = broadcastString .. "a "
        elseif targetPlayer ~= nil then
            local targetPlayer = getPlayerFromMeeple(meeple)
            if targetPlayer.color == player.color then
                broadcastToPlayer("You cannot kill your own Meeple.", player.color)
                unselectMeeples(action.meeples)
                return
            elseif checkDjinn("Boaz", targetPlayer) ~= nil then
                broadcastToPlayer(targetPlayer.name .. "'s Meeples are protected by the Djinn Boaz.", player.color)
                unselectMeeples(action.meeples)
                return
            end
            removeMeeple(targetPlayer.meeples, meeple)
            arrangePlayerMeeples(targetPlayer)
            broadcastString = broadcastString .. targetPlayer.name .. "'s "
        end
        broadcastString = broadcastString .. getObjectFromGUID(meeple).getName()
        broadcastString = broadcastString .. " using " .. #action.tribeMeeples .. " Red Assassins"
        if #action.spentFakirs > 0 then
            broadcastString = broadcastString .. " plus " .. #action.spentFakirs .. " Fakir(s)"
            arrangePlayerResources(player)
            discardResources(action.spentFakirs)
        end
        broadcastString = broadcastString .. "."
        broadcastToAll(broadcastString, player.color)
        activateWhim("Kill", player, getObjectFromGUID(meeple).getName())
    end
    returnMeeples(action.tribeMeeples)
    activateDjinn("Nekir")
    if not activateDjinn("Kandicha", player, {meeples = action.meeples, tile = tile}) then
        returnMeeples(action.meeples)
    end
    resetTileColors()
    player.action = nil
    hidePlayerUi(player)
    djinnReadyUp("Iblis", player, true)
    if tile ~= nil and #tile.meeples == 0 then
        controlTile(player, tile)
    end
    player.selectTwoMeeples = false
    savedData.phase = 4
end

function unselectMeeples(meeples)
    for m = #meeples, 1, -1 do
        riseMeeple(meeples[m], false)
        table.remove(meeples, m)
    end
end

function getPlayerFromMeeple(meeple)
    for p, player in ipairs(savedData.players) do
        for m, meep in ipairs(player.meeples) do
            if meep == meeple then
                return player
            end
        end
    end
    if savedData.yazid ~= nil then
        for m, meep in ipairs(savedData.yazid.meeples) do
            if meep == meeple then
                return savedData.yazid
            end
        end
    end
end

function highlightRedArea(player, var)
    local tile = var.tile
    resetTileColors(tile)
    local amount = #var.tribeMeeples + #var.spentFakirs 
    local dirs = {-1, 1}
    for i = 0, amount do
        for d, dir in ipairs(dirs) do
            local c = tile.col + (dir*i)
            if inBounds(tile.row, c) then
                highlightColumnRed(tile.row, c, amount-i)
            end
        end
    end
end

function highlightColumnRed(row, col, amount)
    local dirs = {-1, 1}
    for i = 0, amount do
        for d, dir in ipairs(dirs) do
            local r = row + (dir*i)
            if inBounds(r, col) then
                local tileObj = getObjectFromGUID(savedData.board[r][col].guid)
                tileObj.UI.setAttribute(tileObj.guid .. "Btn", "color", colors.red)
            end
