function collectItem(player, tribeMeeples, tile)
    local selectedItems = {}
    for i = 1, #tribeMeeples do
        local item = getItem(player)
        table.insert(selectedItems, item)
        prepareItem(item, player)
        Wait.frames(function()
            setVisiblity(getObjectFromGUID(item.guid), item.guid .. "Btn", player.color, hiddenInformationOption == 3)
        end, 10)
    end
    player.selectedItems = selectedItems
    player.selectedMeeples = tribeMeeples
    arrangeObjs(player.selectedItems, tile.guid, 3.5, 3, 3, 0, 180, player)
    player.action = {infoTxt = 'Choose an Item'}
    showPlayerUI(player)
    broadcastToPlayer("Select 1 of the " .. #player.selectedItems .. " drawn Items to keep.", player.color)
end

function clickItem(player, isTurn, id)
    local itemGuid = getGuidFromId(id)
    local selectedItem = isSelectedItemItem(player, itemGuid)
    local playerItem = getPlayerItem(player, itemGuid)
    local itemPlayer = getPlayerFromItem(itemGuid)
    if selectedItem ~= nil and checkIsTurn(player, isTurn) and player.stealDjinn ~= true then
        for i, item in ipairs(player.selectedItems) do
            if item.guid == itemGuid then
                table.insert(player.items, item)
                arrangePlayerItems(player)
            else
                discardItem(item)
            end
        end
        hidePlayerUi(player)
        player.selectedItems = nil
        if player.action == nil or player.action.useRing ~= true then
            broadcastToAll(player.name .. " gained an Item and collected " .. #player.selectedMeeples .. " Purple Artisans.", player.color)
            collectMeeple(player, player.selectedMeeples, true)
        else
            setVisiblity(getObjectFromGUID(itemGuid), itemGuid .. "Btn", player.color, hiddenInformationOption == 3)
            Wait.frames(function()
                useAndDiscardItem(player, player.action.item)
            end, 10)
        end
    elseif playerItem ~= nil and player.stealDjinn ~= true
        and checkIsTurn(player, isTurn) 
        and playerItem.func ~= nil 
        and (savedData.phase == 2 
            or (savedData.phase == 5 and playerItem.phase2 ~= true)
            or playerItem.active == true) then
        local isSelected = player.action ~= nil 
                            and player.action.item ~= nil 
                            and player.action.item.name == playerItem.name
        if player.itemUsed == true then
            broadcastToPlayer("You may only use one item per turn.", player.color)
            return
        elseif isSelected then
            arrangePlayerItems(player)
        elseif player.busy and playerItem.active ~= true then
            return
        else
            local itemObj = getObjectFromGUID(playerItem.guid)
            local cloneObj = getObjectFromGUID(playerItem.clone)
            local pos = itemObj.getPosition()
            pos.y = pos.y + 2
            itemObj.setPositionSmooth(pos, false, true)
            pos.y = pos.y - 0.005
            cloneObj.setPositionSmooth(pos, false, true)
        end
        hidePlayerUi(player)
        player.busy = true
        if not isSelected then
            player.previousAction = player.action
        end
        _G[playerItem.func](player, playerItem, isSelected)
        if isSelected then
            player.busy = false
            player.action = player.previousAction
            hidePlayerUi(player)
            showPlayerUI(player)
        end
    elseif itemPlayer ~= nil then
        if savedData.hotseatEnabled then
            player = itemPlayer
        end
        if itemPlayer.color == player.color 
            and player.stealItem == true 
            and player.color ~= savedData.currentTurn then
            stealItem(player, nil, itemGuid)
        end
    elseif findStolenItem(itemGuid, player) ~= nil then
        if player.waitForThief == 0 then
            stealItem(player, player.selectedDjinn, itemGuid)
        else
            broadcastToPlayer("Wait for people to finish discarding the Items.", player.color)
        end
    end
end

