function discardItem(item)
    local discardPos = {
        {x = '-6', z = '52'},
        {x = '-6', z = '49'},
        {x = '-9', z = '49'},
        {x = '-12', z = '49'},
        {x = '-12', z = '52'},
        {x = '-3', z = '52'},
        {x = '-3', z = '49'},
        {x = '-3', z = '46'},
        {x = '-6', z = '46'},
        {x = '-9', z = '46'},
        {x = '-12', z = '46'},
        {x = '-15', z = '46'},
        {x = '-15', z = '49'},
        {x = '-15', z = '52'},
        {x = '0', z = '52'},
        {x = '-15', z = '43'},
        {x = '-15', z = '55'},
        {x = '-9', z = '52'}
    }
    local rot = {x = 0, y = 180, z = 180}
    table.insert(savedData.itemsDiscard, item)
    getObjectFromGUID(item.guid).UI.setAttribute(item.guid .. "Btn", "color", "")
    for i, item in ipairs(savedData.itemsDiscard) do
        local pos = discardPos[i]
        for g, guid in ipairs({item.guid, item.clone}) do
            local itemObj = getObjectFromGUID(guid)
            if g == 1 then
                pos.y = 1.12
                itemObj.setInvisibleTo({})
                setVisiblity(itemObj, guid .. "Btn", "")
            else
                pos.y = 1.115
                itemObj.setInvisibleTo(getPlayerList(nil, true))
            end
            itemObj.setRotationSmooth(rot, false, true)
            itemObj.setPositionSmooth(pos, false, true)
        end
    end
end

