function discardResources(resourceList)
    local objGroup = {}
    local discardGroup = nil
    if #resourceList == 0 then
        return
    elseif #resourceList > 1 then
        for r, resource in ipairs(resourceList) do
            table.insert(objGroup, getObjectFromGUID(resource))
        end
        discardGroup = group(objGroup)[1]
    else
        discardGroup = getObjectFromGUID(resourceList[1])
    end
    local pos = {x = 17.50, y = 1.01, z = 49.00}
    local rot = {x = 0, y = 180, z = 0}
    if savedData.resourceDiscard ~= nil then
        discardGroup = group({getObjectFromGUID(savedData.resourceDiscard), discardGroup})[1]
    end
    Wait.frames(function()
        discardGroup.UI.setXml("")
        discardGroup.setLock(true)
        discardGroup.interactable = false
        discardGroup.setHiddenFrom({})
        discardGroup.setRotationSmooth(rot, false, true)
        discardGroup.setPositionSmooth(pos, false, true)
        savedData.resourceDiscard = discardGroup.guid
    end, 5)
end

