function placePalmTree(player, tile, alt)
    local palmTree = nil
    local usedDjinn = false
    if player.palmTree ~= nil then
        palmTree = player.palmTree
    else
        palmTree = getPalmTree(player)
    end
    if alt ~= nil then
        tile = tile.tile
    end
    if palmTree ~= nil then
        if player.skip ~= true then
            if player.action ~= nil and player.action.djinn ~= nil then
                spendBroadcastDjinnPaidUsed(player, player.action, "to place a Palm Tree on an Oasis Tile")
                usedDjinn = true
            else
                broadcastToAll(player.name .. " placed a Palm Tree on the " .. tile.name .. " Tile.", player.color)
            end
        end
        if player.skip == true
            or not djinnReadyUp("Lamia", player) then
            table.insert(tile.buildings, palmTree)
            arrangeTileItems(tile)
            djinnReadyUp("Lamia", player, true)
            player.skip = nil
            player.palmTree = nil
        else
            broadcastToPlayer("Use the Djinn or click 'Place Palm Tree'.", player.color)
            player.action = {
                passTxt = 'Place Palm Tree',
                infoTxt = "Select Djinn or Click 'Place Palm Tree'",
                passFunc = 'placePalmTree',
                tile = tile
            }
            player.palmTree = palmTree
            player.skip = true
            player.adjacentTile = tile
            showPlayerUI(player)
            return
        end
    end
    if not usedDjinn then
        endRound(player)
    end
end

