function gainDjinn(player, action, alt)
    hidePlayerUi(player)
    if action.djinn == nil then
        player.action = {
            djinnFunc = 'gainDjinn',
            passTxt = 'Skip Gaining Djinn',
            infoTxt = 'Select a Djinn to Gain',
            passFunc = 'endRound'
        }
        showPlayerUI(player)
        return
    elseif alt == nil and #checkDjinnCost(player, {{2,0},{1,1}}) > 1 then
        broadcastToPlayer("Choose what to pay for the Djinn.", player.color)
        player.action = {
            djinnFunc = 'gainDjinn',
            passTxt = 'Pay 2 Elders',
            djinn = action.djinn,
            passFunc = 'gainDjinn',
            passAltTxt = 'Pay 1 Elder and 1 Fakir',
            infoTxt = 'or'
        }
        showPlayerUI(player)
        return
    elseif (alt == nil or not alt) and #checkDjinnCost(player, {{2,0}}) == 1 then
        spendElderFakirs(player, {2,0})
    elseif (alt == nil or alt) and #checkDjinnCost(player, {{1,1}}) == 1 then
        spendElderFakirs(player, {1,1})
    end
    takeDjinnFromRow(player, action.djinn)
    broadcastToAll(player.name .. " gained the Djinn " .. getObjectFromGUID(action.djinn).getName() .. " from the Sacred Place Tile.", player.color)
    player.action = nil
    endRound(player)
end

