function getDjinnInfo(djinnGuid)
    local djinnInfo = {}
    local djinnName = getObjectFromGUID(djinnGuid).getName()
    --djinnName = "Utug"
    for d, djinn in ipairs(djinns) do
        if djinnName == djinn.name then
            djinnInfo = table.copy(djinn)
            djinnInfo.guid = djinnGuid
            djinnInfo.used = false
            return djinnInfo
        end
    end
    for t, thief in ipairs(thieves) do
        if thief.name == djinnName then
            djinnInfo = table.copy(thief)
            djinnInfo.guid = djinnGuid
            djinnInfo.used = false
            djinnInfo.active = false
            djinnInfo.func = 'activateThief'
            djinnInfo.points = 6
            djinnInfo.cost = {{0,0}}
            if djinnInfo.qty == nil then
                djinnInfo.qty = 1
            end
            return djinnInfo
        end
    end
    return {guid = djinnGuid}
end

