function spendMeeples(player, amount, meepleName)
    local spentMeeples = {}
    if amount == 0 then
        return
    end
    for i = #player.meeples, 1, -1 do
        if getObjectFromGUID(player.meeples[i]).getName() == meepleName then
            table.insert(spentMeeples, table.remove(player.meeples, i))
        end
        if #spentMeeples == amount then
            returnMeeples(spentMeeples)
            arrangePlayerMeeples(player)
