function buyItem(player, action, alt)
    local costString = ""
    if action.buying == nil and alt then
        endRound(player)
        return
    elseif action.buying == nil 
        and #getFakirs(player) >= 2 
        and #getMeeples(player, 'Purple Artisan') > 0 then
        broadcastToPlayer("Choose what to pay for the Item.", player.color)
        action.passTxt = "Pay 1 Artisan"
        action.buying = true
        action.passAltTxt = "Pay 2 Fakirs"
        action.passFunc = "buyItem"
        player.action = action
        showPlayerUI(player)
        return
    elseif (action.buying == nil or not alt) 
        and #getMeeples(player, "Purple Artisan") > 0 then
        spendMeeples(player, 1, "Purple Artisan")
        costString = "1 Artisan"
    elseif (action.buying == nil or alt) 
        and #getFakirs(player) >= 2 then
        spendFakirs(player, 2)
        costString = "2 Fakirs"
    end
    givePlayerTopItem(player)
    broadcastToAll(player.name .. " paid " .. costString .. " to gain an Item from the Workshop Tile.", player.color)
    endRound(player)
end

function givePlayerTopItem(player)
