        arrangePlayerWhims(player)
        activateDjinn("Ganapati")
        Wait.frames(function()
            checkPlayerWhims(player)
        end, 10)
    end
    endRound(player)
end

function arrangePlayerWhims(player)
    arrangeObjs(player.whims, player.board, 20, 4.5, 1)
end

function endRound(player, action, skipPhase)
    if player.busy == true then
        return
    end
    updateScore()
    arrangeResources()
    resetResourceColors()
    resetTileColors()
    setDjinnColor("")
    hidePlayerUi(player)
    if skipPhase ~= true then
        savedData.phase = 5
    end
    checkPlayerWhims(player)
    if ((checkActiveDjinns(player) 
            or checkActiveItems(player)
            or (countGold(player) < 24 and #player.resources > #getFakirs(player))
            or #player.resources > 0 and autoEndTurn == false))
        and (action == nil 
            or action.skipActions ~= true) then
        broadcastToPlayer("Use any Djinns/Items or sell any Resources you want, then click 'End Turn' when done.", player.color)
        player.action = {
            passTxt = 'End Turn',
            passFunc = 'endRound',
            infoTxt = 'Click When Ready',
            skipActions = true
        }
        showPlayerUI(player)
    else
        player.previousAction = nil
        player.action = nil
        Wait.frames(function()
            for d, djinn in ipairs(player.djinns) do
                if djinn.used == true then
                    djinn.used = false          
                end         
                if djinn.tribe ~= nil then
                    djinn.active = false
                end
                getObjectFromGUID(djinn.guid).UI.setAttribute(djinn.guid .. "Btn", "color", "")
            end
            for i, item in ipairs(player.items) do
                getObjectFromGUID(item.guid).UI.setAttribute(item.guid .. "Btn", "color", "")
            end
			player.itemUsed = false
            setNextTurn()
        end, 5)
    end
end

function checkActiveDjinns(player)
    if savedData.currentTurn ~= player.color then
        return
    end
    local djinnActive = false
    for d, djinn in ipairs(player.djinns) do
        if djinn.active and djinn.ready == nil and not djinn.used 
            and (djinn.cost == nil or #checkDjinnCost(player, djinn.cost) ~= 0) then
            getObjectFromGUID(djinn.guid).UI.setAttribute(djinn.guid .. "Btn", "color", colors.solidGold)
            djinnActive = true
        else
            getObjectFromGUID(djinn.guid).UI.setAttribute(djinn.guid .. "Btn", "color", "")
        end
    end
    return djinnActive
end

function checkActiveItems(player)
    if savedData.currentTurn ~= player.color then
        return
    end
    local itemActive = false
    for i, item in ipairs(player.items) do
        if item.func ~= nil and (savedData.phase == 2 or item.phase2 == nil) then
            Wait.frames(function()
                getObjectFromGUID(item.guid).UI.setAttribute(item.guid .. "Btn", "color", colors.solidGold)
            end, 5)
            itemActive = true
        else
            Wait.frames(function()
                getObjectFromGUID(item.guid).UI.setAttribute(item.guid .. "Btn", "color", "")
            end, 5)
        end
        item.active = false
    end
    return itemActive
end

--Whim Cards
function checkPlayerWhims(player)
    for w, whim in ipairs(player.whims) do
        if whim.checkFunc ~= nil then
            _G[whim.checkFunc](player, whim)
        end
    end
end

function validateWhimCard(player, whim)
    for c, card in ipairs(player.whims) do
        if card.name == whim.name then
            broadcastToAll(player.name .. " gained " .. whim.gc .. " GC for validating the '" .. whim.name .. "' Whim of the Sultan Card.", player.color)
            discardWhimCard(player, whim)
            takeGoldCoins(player, whim.gc)
            table.remove(player.whims, c)
            arrangePlayerWhims(player)
            Wait.frames(function()
                checkPlayerWhims(player)
            end, 10)
            return
        end
    end
end

function getWhimInfo(whimGuid)
    local whimName = getObjectFromGUID(whimGuid).getName()
    for w, whim in ipairs(whims) do
        if whimName == whim.name then
            local whimInfo = table.copy(whim)
            whimInfo.guid = whimGuid
            return whimInfo
        end
    end
end

function discardWhimCard(player, whim)
    local whimObj = getObjectFromGUID(whim.guid)
    local boardObj = getObjectFromGUID(player.board)
    local pos = {x = -15.5, y = 0.05, z = 13.70}
    local rot = boardObj.getRotation()
    rot.y = rot.y + 75
    local tPos = getTransformPosition(boardObj, pos)
    if player.whimDiscard == nil then
        whimObj.setRotationSmooth(rot, false, true)
        whimObj.setPositionSmooth(tPos, false, true)
        whimObj.UI.setXml("")
        player.whimDiscard = whimObj.guid
    else
        local whimDiscardObj = getObjectFromGUID(player.whimDiscard)
        local whimDiscardObj = group({whimDiscardObj, whimObj})[1]
        Wait.frames(function()
            whimDiscardObj.setLock(true)
            whimDiscardObj.interactable = false
            player.whimDiscard = whimDiscardObj.guid
            whimDiscardObj.setRotationSmooth(rot, false, true)
            whimDiscardObj.setPositionSmooth(tPos, false, true)
        end, 10)
    end
end

function activateWhim(whimName, player, var)
    for w, whim in ipairs(player.whims) do
        if string.match(whim.name, whimName) then
            _G[whim.func](player, whim, var)
        end
    end
end

function whimKillMeeple(player, whim, meepleName)
    local req = string.split(whim.name, ' ')
    local reqName = req[2] .. " " .. req[3]
    if reqName == meepleName then
        validateWhimCard(player, whim)
    end
end

function whimBuilderAction(player, whim, total)
    if total >= 15 then
        validateWhimCard(player, whim)
    end
end

function whimOwnResource(player, whim)
    local count = 0
    for r, resource in ipairs(player.resources) do
        if getObjectFromGUID(resource).getName() == whim.resource then
            count = count + 1
            if count == whim.count then
                validateWhimCard(player, whim)
                return
            end
        end
    end
end

function whimValidateWhims(player, whim)
    if player.whimDiscard ~= nil 
        and getObjectFromGUID(player.whimDiscard).getQuantity() >= 2 then
        validateWhimCard(player, whim)
    end
end

function whimControlDjinns(player, whim)
    if #player.djinns >= whim.count then
        validateWhimCard(player, whim)
    end
end

function whimOwnMeeples(player, whim)
    local count = 0
    local reqColor = string.split(whim.name, ' ')[3]
    for m, meeple in ipairs(player.meeples) do
        if string.match(getObjectFromGUID(meeple).getName(), reqColor) then
            count = count + 1
            if count == whim.count then
                validateWhimCard(player, whim)
                return
            end
        end
    end
end

function whimPreciousItems(player, whim)
    local count = 0
    local reqColor = string.split(whim.name, ' ')[3]
    for i, item in ipairs(player.items) do
        if item.name == "Precious Item" then
            count = count + 1
            if count == whim.count then
                validateWhimCard(player, whim)
                return
            end
        end
    end
end

function whimControlCornerTile(player, whim)
    local lastRow = #savedData.board
    local tileCood = {{1,1},{1,6},{lastRow,1},{lastRow,6}}
    for c, cood in ipairs(tileCood) do
        local tile = savedData.board[cood[1]][cood[2]]
        if checkTileControl(player, tile) then
            validateWhimCard(player, whim)
            return
        end
    end
end

function whimControlAdjacentTiles(player, whim)
    local checkDirs = {{{1,0},{-1,0}},{{0,1},{0,-1}}}
    for r = 1, #savedData.board do
        for c = 1, 6 do
            local tile = savedData.board[r][c]
            if checkTileControl(player, tile) then
                for u, lineUps in ipairs(checkDirs) do
                    local count = 1
                    for l, line in ipairs(lineUps) do
                        local tRow = r+line[1]
                        local tCol = c+line[2]
                        if not (tRow < 1 or tRow > #savedData.board or tCol < 1 or tCol > 6) then
                            local lineTile = savedData.board[tRow][tCol]
                            if checkTileControl(player, lineTile) then
                                count = count + 1
                                if count == 3 then
                                    validateWhimCard(player, whim)
                                    return
                                end
                            else
                                break
                            end
                        end
                    end
                end
            end
        end
    end
end

function checkTileControl(player, tile)
    if tile.camel ~= nil 
        and getCamelColor(tile.camel) == player.color then
        return true
    end
    return false
end

function whimControlPlaces(player, whim)
    local count = 0
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if checkTileControl(player, tile) 
                and tile.name == whim.place then
                count = count + 1
                if count == whim.count then
                    validateWhimCard(player, whim)
                    return
                end
            end
        end
    end
end

function whimControlMarkets(player, whim)
    local small = false
    local large = false
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if checkTileControl(player, tile) then
                if tile.name == "Small Market" then
                    small = true
                elseif tile.name == "Large Market" then
                    large = true
                end
                if small and large then
                    validateWhimCard(player, whim)
                    return
                end
            end
        end
    end
end

function whimControlBuildings(player, whim)
    local count = 0
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if checkTileControl(player, tile) then
                for b, building in ipairs(tile.buildings) do
                    if getObjectFromGUID(building).getName() == whim.building then
                        count = count + 1
                        if count == whim.count then
                            validateWhimCard(player, whim)
                            return
                        end
                    end
                end
            end
        end
    end
end

