function fluteMoveMeeple(player, meepleGuid)
    local selectedTile = player.action.selectedTile
    local meepleTiles = player.action.meepleTiles
    local found = false
    for m, meeple in ipairs(selectedTile.pendingMeeples) do
        if meeple == meepleGuid then
            table.remove(selectedTile.pendingMeeples, m)
            for t, tile in ipairs(meepleTiles) do
                if tile.meeple == meepleGuid then
                    table.insert(tile.tile.meeples, meepleGuid)
                    table.remove(meepleTiles, t)
                    arrangeTileItems(tile.tile)
                end
            end
            found = true
            break
        end
    end
    if not found then
        for t, tile in ipairs(getAdjacentTilesWithMeeples(selectedTile)) do
            for m, meeple in ipairs(tile.meeples) do
                if meeple == meepleGuid then
                    if #meepleTiles < 5 then
                        table.insert(meepleTiles, {
                            meeple = meepleGuid,
                            tile = tile
                        })
                        table.remove(tile.meeples, m)
                        table.insert(selectedTile.pendingMeeples, meepleGuid)
                        arrangeTileItems(selectedTile)
                    else
                        broadcastToPlayer("You can only select up to 5 Meeples to move.", player.color)
                    end
                    found = true
                    break
                end
            end
        end
    end
    if not found then
        broadcastToPlayer("You can only select Meeples from adjacent Tiles.", player.color)
    elseif #meepleTiles > 0 then
        player.action.passTxt = "Move " .. #meepleTiles .. " Meeple(s)"
        player.action.passFunc = 'fluteMoveMeeples'
        showPlayerUI(player)
    else
        hidePlayerUi(player)
    end
end

function fluteReturnAllMeeples(selectedTile, meepleTiles)
    for t, tile in ipairs(meepleTiles) do
        table.insert(tile.tile.meeples, selectedTile.pendingMeeples[t])
        arrangeTileItems(tile.tile)
    end
    selectedTile.pendingMeeples = {}
end

function fluteMoveMeeples(player, action)
    local selectedTile = action.selectedTile
    for m, meeple in ipairs(selectedTile.pendingMeeples) do
        table.insert(selectedTile.meeples, meeple)
    end
    selectedTile.pendingMeeples = {}
    arrangeTileItems(selectedTile)
    useAndDiscardItem(player, action.item)
end

function getAdjacentTilesWithMeeples(tile, highlightColor)
    local meepleTiles = {}
    local dirs = {{1,0},{0,1},{-1,0},{0,-1}}
    for d, dir in ipairs(dirs) do
        if inBounds(tile.row+dir[1], tile.col+dir[2]) then
            local adjacentTile = savedData.board[tile.row+dir[1]][tile.col+dir[2]]
            if not isBlockedByMountains(tile, adjacentTile)
                and not isBlockedByMountains(adjacentTile, tile)
                and #adjacentTile.meeples > 0 then
                table.insert(meepleTiles, adjacentTile)
                if highlightColor ~= nil then
                    getObjectFromGUID(adjacentTile.guid).UI.setAttribute(adjacentTile.guid .. "Btn", "color", highlightColor)
                end
            end
        end
    end
    return meepleTiles
end

