function useAndDiscardItem(player, discardedItem, endR)
    broadcastToAll(player.name .. " used the Item '" .. discardedItem.name .. "' to " .. discardedItem.use .. ".", player.color)
    player.itemUsed = true
    for i, item in ipairs(player.items) do
        if item.guid == discardedItem.guid then
            table.remove(player.items, i)
            discardItem(discardedItem)
            arrangePlayerItems(player)
            break
        end
    end
    if endR ~= false then
        hidePlayerUi(player)
        player.busy = false
        player.action = nil
        if savedData.phase == 5 then
            endRound(player)
        else
            checkActiveItems(player)
        end
    end
end

--Thief Actions
function activateThieves(player, meepleName)
    for d, djinn in ipairs(player.djinns) do
        if djinn.tribe == meepleName then
            djinn.active = true 
            return
        end
    end
end

function findStolenItem(guid, player)
    for i, item in ipairs(player.stolenItems) do
        if item == guid or item.guid == guid then
            return item
        end
    end
end

function activateThief(player, action)
    local thief = action.djinn
    broadcastToAll(player.name .. " used the Thief " .. thief.name .. " to force all players to discard " .. thief.stealName .. ".", player.color)
    player.stolenItems = {}
    player.waitForThief = #savedData.players - 1
    for p, opp in ipairs(savedData.players) do
        if opp.color ~= player.color then
            if checkDjinn("Morgiane", opp) ~= nil then
                player.waitForThief = player.waitForThief - 1
                if #savedData.players == 2 then
                    thiefResolveSteal(player, thief)
                end
            else
                _G[thief.stealFunc](opp, thief)
            end
        end
    end
end

function sendToThief(objs, arrange)
    local currentPlayer = getPlayerFromColor(savedData.currentTurn)
    currentPlayer.waitForThief = currentPlayer.waitForThief - 1
    for o, obj in ipairs(objs) do
        table.insert(currentPlayer.stolenItems, obj)
    end
    if arrange then
        local offset = currentPlayer.selectedDjinn.offset
        local rotZ = currentPlayer.selectedDjinn.rotZ
        arrangeObjs(currentPlayer.stolenItems, currentPlayer.board, 2.75, offset, 3, nil, rotZ, currentPlayer)
    end
    if currentPlayer.waitForThief == 0 then
        thiefResolveSteal(currentPlayer, currentPlayer.selectedDjinn)
    end
end

function thiefResolveSteal(player, thief)
    if #player.stolenItems == 0 then
        broadcastToPlayer("No one had " .. thief.stealName .. " to steal.", player.color)
        Wait.frames(function()
            selectDjinn(player)
        end, 10)
    else
        _G[thief.stealFunc](player, thief)
    end
end

function stealYellowVizier(player, thief)
    if player.color == savedData.currentTurn then
        table.insert(player.meeples, table.remove(player.stolenItems, 1))
        arrangePlayerMeeples(player)
        returnMeeples(player.stolenItems)
        discardThief(player, thief)
    else
        local currentPlayer = getPlayerFromColor(savedData.currentTurn)
        for m, meeple in ipairs(player.meeples) do
            if getObjectFromGUID(meeple).getName() == 'Yellow Vizier' then
                sendToThief({table.remove(player.meeples, m)})
                arrangePlayerMeeples(player)
                return
            end
        end
        sendToThief({})
    end
end

function getControlledTiles(player, building)
    local controlTiles = {}
    for r, row in ipairs(savedData.board) do
        for t, tile in ipairs(row) do
            if tile.camel ~= nil and getCamelColor(tile.camel) == player.color then
                if building ~= true or #tile.buildings > 0 then
                    table.insert(controlTiles, tile)
                end
            end
        end
    end
    return controlTiles
end

function stealBuilding(player, thief, selTile)
    if player.color == savedData.currentTurn then
        if player.selectedBuilding == nil then
            local buildings = {}
            for i, item in ipairs(player.stolenItems) do
                table.insert(buildings, table.remove(item.buildings, #item.buildings))
            end
            for b, building in ipairs(buildings) do
                if getObjectFromGUID(building).getName() == 'Palace' then
                    player.selectedBuilding = table.remove(buildings, b)
                    break
                end
            end
            if player.selectedBuilding == nil then
                player.selectedBuilding = table.remove(buildings, 1)
            end
            discardBuildings(buildings)
            player.stolenItems = getControlledTiles(player)
        end
        if #player.stolenItems == 1 then
            table.insert(player.stolenItems[1].buildings, player.selectedBuilding)
            arrangeTileItems(player.stolenItems[1])
        elseif selTile ~= nil then
            for i, tile in ipairs(player.stolenItems) do
                if tile.guid == selTile.guid then
                    table.insert(tile.buildings, player.selectedBuilding)
                    arrangeTileItems(tile)
                end
            end
        else
            for i, item in ipairs(player.stolenItems) do
                getObjectFromGUID(item.guid).UI.setAttribute(item.guid .. "Btn", "color", colors.white)
            end
            local buildingName = getObjectFromGUID(player.selectedBuilding).getName()
            broadcastToPlayer("Select a Tile to place a " .. buildingName .. ".", player.color)
            player.action = {infoTxt = "Select a Tile to place a " .. buildingName}
            showPlayerUI(player)
            return
        end
        player.selectedBuilding = nil
        player.stealBuilding = nil
        player.stolenItems = {}
        resetTileColors()
        discardThief(player, thief)
    else
        local currentPlayer = getPlayerFromColor(savedData.currentTurn)
        local controlledTiles = getControlledTiles(player, true)
        if #controlledTiles == 0 then
            sendToThief({})
        elseif #controlledTiles == 1 or selTile ~= nil then
            for t, tile in ipairs(controlledTiles) do
                if (selTile == nil or tile.guid == selTile.guid) then
                    local building = tile.buildings[#tile.buildings]--table.remove(tile.buildings, #tile.buildings)
                    local buildingObj = getObjectFromGUID(building)
                    local pos = buildingObj.getPosition()
                    pos.y = 3
                    buildingObj.setPositionSmooth(pos, false, true)
                    player.stealBuilding = nil
                    hidePlayerUi(player)
                    getObjectFromGUID(tile.guid).UI.setAttribute(tile.guid .. "Btn", "color", getTransparentColor(player.color))    
                    sendToThief({tile})
                else
                    getObjectFromGUID(tile.guid).UI.setAttribute(tile.guid .. "Btn", "color", "")
                end
            end
        else
            displayThiefMessage(player, thief)
            for t, tile in ipairs(controlledTiles) do
                getObjectFromGUID(tile.guid).UI.setAttribute(tile.guid .. "Btn", "color", getTransparentColor(player.color))
            end
            currentPlayer.stealBuilding = true
            player.stealBuilding = true
        end
    end
end

function discardBuildings(buildings)
    local palmTreeCount = 0
    local palaceCount = 0
    local zPos = {
        {z = 42.75},
        {z = 44.25},
        {z = 45.75},
        {z = 47.25},
    }
    for b, building in ipairs(buildings) do
        local buildingObj = getObjectFromGUID(building)
        local pos = nil
        if buildingObj.getName() == "Palm Tree" then
            palmTreeCount = palmTreeCount + 1
            pos = zPos[palmTreeCount]
            pos.x = -28.25
            table.insert(savedData.palmTrees, building)
        else
            palaceCount = palaceCount + 1
            pos = zPos[palaceCount]
            pos.x = -19.25
            table.insert(savedData.palaces, building)
        end
        pos.y = 0.99
        buildingObj.setPositionSmooth(pos, false, true)
    end
end

function stealCamel(player, thief, selTile)
    if player.color == savedData.currentTurn then
        if #player.stolenItems == 1 then
            controlTile(player, player.stolenItems[1])
        elseif selTile ~= nil then
