end

function stealItem(player, thief, itemGuid)
    if player.color == savedData.currentTurn then
        if #player.stolenItems == 1 then
            table.insert(player.items, table.remove(player.stolenItems, 1))
        elseif itemGuid ~= nil then
            for i, item in ipairs(player.stolenItems) do
                if item.guid == itemGuid then
                    table.insert(player.items, item)
                else
                    discardItem(item)
                end
            end
            player.stolenItems = {}
        else
            for i, item in ipairs(player.stolenItems) do
                getObjectFromGUID(item.guid).UI.setAttribute(item.guid .. "Btn", "color", colors.solidGold)
            end
            broadcastToPlayer("Select a Item to keep.", player.color)
            player.action = {infoTxt = "Select a Item to Keep"}
            showPlayerUI(player)
            return
        end
        Wait.frames(function()
            arrangePlayerItems(player)
        end, 10)
        discardThief(player, thief)
        player.stealItem = nil
    else
        local currentPlayer = getPlayerFromColor(savedData.currentTurn)
        if #player.items == 0 then
            sendToThief({})
        elseif #player.items == 1 or itemGuid ~= nil then
            for i, item in ipairs(player.items) do
                getObjectFromGUID(item.guid).UI.setAttribute(item.guid .. "Btn", "color", "")
            end
            for i, item in ipairs(player.items) do
                if (itemGuid == nil or item.guid == itemGuid) then
                    sendToThief({table.remove(player.items, i)}, true)
                    Wait.frames(function()
                        arrangePlayerItems(player)
                    end, 10)
                    player.stealItem = nil
                    hidePlayerUi(player)
                    return
                end
            end
        else
            displayThiefMessage(player, thief)
            for i, item in ipairs(player.items) do
                getObjectFromGUID(item.guid).UI.setAttribute(item.guid .. "Btn", "color", colors.solidGold)
            end
            currentPlayer.stealItem = true
            player.stealItem = true
        end
    end
end

