function stealResources(player, thief, resources)
    if player.color == savedData.currentTurn then
        if #player.stolenItems <= 2 then
            for i, item in ipairs(player.stolenItems) do
                table.insert(player.resources, item)
            end
            Wait.frames(function()
                arrangePlayerResources(player)
            end, 10)
        elseif resources ~= nil then
            local resourceDiscard = {}
            for i, item in ipairs(player.stolenItems) do
                local found = false
                for r, resource in ipairs(resources) do
                    if resource == item then
                        table.insert(player.resources, item)
                        found = true
                        break
                    end
                end
                if not found then
                    table.insert(resourceDiscard, item)
                end
            end
            arrangePlayerResources(player)
            discardResources(resourceDiscard)
        else
            for i, item in ipairs(player.stolenItems) do
                getObjectFromGUID(item).setHiddenFrom({})
                setVisiblity(getObjectFromGUID(item), item .. "Btn", player.color, hiddenInformationOption == 3)
                getObjectFromGUID(item).UI.setAttribute(item .. "Btn", "color", colors.solidGold)
            end
            broadcastToPlayer("Select 2 Resources to keep.", player.color)
            player.action = {infoTxt = "Select 2 Resources to Keep"}
            showPlayerUI(player)
            player.selectedResources = {}
            return
        end
        player.stolenItems = {}
        discardThief(player, thief)
        player.stealResources = nil
    else
        local currentPlayer = getPlayerFromColor(savedData.currentTurn)
        if #player.resources == 0 then
            sendToThief({})
        elseif #player.resources <= 2 then
            sendToThief(player.resources, true)
            player.resources = {}
        elseif resources ~= nil then
            for r = #player.resources, 1, -1 do
                for s, sel in ipairs(resources) do
                    if sel == player.resources[r] then
                        table.remove(player.resources, r)
                        break
                    end
                end
            end
            sendToThief(resources, true)
            Wait.frames(function()
                arrangePlayerResources(player)
            end, 10)
            player.stealResources = nil
            hidePlayerUi(player)
        else
            displayThiefMessage(player, thief)
            for r, resource in ipairs(player.resources) do
                getObjectFromGUID(resource).UI.setAttribute(resource .. "Btn", "color", colors.solidGold)
            end
            currentPlayer.stealResources = true
            player.stealResources = true
            player.selectedResources = {}
        end
    end
end

function displayThiefMessage(player, thief)
    broadcastToPlayer("Select " .. thief.stealName .. " to discard.", player.color)
    player.action = {infoTxt = "Select " .. thief.stealName .. " to Discard"}
    showPlayerUI(player)
end

function discardThief(player, thief)
    for d, djinn in ipairs(player.djinns) do
        if djinn.guid == thief.guid then
            table.remove(player.djinns, d)
            Wait.frames(function()
                arrangePlayerDjinns(player)
            end, 10)
            break
        end
    end
    local thievesDeckPos = savedData.thievesDeckPos
    thievesDeckPos.y = -2
    local thievesDrawObj = getObjectFromGUID(thief.guid)
    thievesDrawObj.UI.setXml("")
    Wait.frames(function()
        thievesDrawObj.setPositionSmooth(thievesDeckPos, false, true)
    end, 10)
    endRound(player)
end

--Djinn Actions
function activateDjinn(djinnName, player, var)
    for _, p in ipairs(savedData.players) do
        for d, djinn in ipairs(p.djinns) do
            if djinn.name == djinnName then
                if player == nil or player.color == p.color then 
                    _G[djinn.func](p, djinn, var)
                    return true
                end
            end
        end
    end
    return false
end

function checkDjinn(djinnName, player)
    if gameMode > 1 and player.color == 'Blue' then
        return nil
    end
    for d, djinn in ipairs(player.djinns) do
        if djinn.name == djinnName then
            if djinn.scoreTxt ~= nil and savedData.endGame then
                local djinnObj = getObjectFromGUID(djinn.guid)
                djinnObj.UI.setAttribute(djinn.guid .. "Btn", "color", colors.solidGold)
                Wait.time(function()
                    djinnObj.UI.setAttribute(djinn.guid .. "Btn", "color", "")
                end, scoringSpeed*1.5)
                broadcastToPlayer(djinn.scoreTxt .. " from the Djinn " .. djinn.name .. ".", player.color)
            end
            return djinn
        end
    end
    return nil
end

function djinnGainGCKali(player, djinn, pushedBids)
    local isTurn = savedData.currentTurn == player.color
    for b, bid in ipairs(pushedBids) do
        if bid.color == player.color and not isTurn then
            djinnGainGC(player, djinn, 'pushing back thier Turn Marker on the Turn Order Track')
        elseif bid.color ~= player.color and isTurn then
            djinnGainGC(player, djinn, "pushing back an opponent's Turn Marker on the Turn Order Track")
        end
    end
end

function djinnGainGC(player, djinn, reason)
    if reason == nil then
        reason = djinn.reason
    end
    local broadcastString = player.name .. " collected "
    local opponentAction = player.color ~= savedData.currentTurn
    local amount = 1
    if opponentAction then
        amount = 2
    end
    broadcastString = broadcastString .. amount .. " GC from the Djinn " .. djinn.name .. " for "
    if opponentAction then
        broadcastString = broadcastString .. getPlayerFromColor(savedData.currentTurn).name .. " "
    end
    takeGoldCoins(player, amount)
    broadcastToAll(broadcastString .. reason .. ".", player.color)
end

function djinnKandichaAction(player, djinn, var)
    local broadcastString = player.name
    local redCount = 0
    for m, meeple in ipairs(var.meeples) do
        if m > 1 and redCount == 0 then
            broadcastString = broadcastString .. ", and"
        end
        local meepleObj = getObjectFromGUID(meeple)
        local mName = meepleObj.getName()
        if mName == 'Blue Builder' then
            scoreAdjacentBlueTiles(player, {meeple}, var.tile, true)
            broadcastString = broadcastString .. " collected GC for the " .. mName
        elseif mName == 'Green Merchant' then
            if #savedData.resources > 0 then
                table.insert(player.resources, table.remove(savedData.resources, 1))
            end
            arrangePlayerResources(player)
            arrangeResources()
            broadcastString = broadcastString .. " drew the first card from the Resource row for the " .. mName
        elseif mName == 'Purple Artisan' then
            broadcastString = broadcastString .. " took the first Item from the Item pile and"
            givePlayerTopItem(player)
        elseif mName == 'Red Assassin' then
            redCount = redCount + 1
        end
        if mName == 'White Elder' 
            or mName == 'Yellow Vizier'
            or mName == 'Purple Artisan' then
            table.insert(player.meeples, meeple)
            getObjectFromGUID(meeple).highlightOff()
            arrangePlayerMeeples(player)
            broadcastString = broadcastString .. " collected a " .. mName
            if mName == 'Yellow Vizier' then
                Wait.frames(function()
                    activateDjinn("Dhenim")
                end, 1)
            end
        else
            returnMeeples({meeple})
        end
    end
    broadcastString = broadcastString .. " using Djinn " .. djinn.name .. "'s power."
    if redCount < #var.meeples then
        broadcastToAll(broadcastString, player.color)
    end
end

function djinnTakeResource(player, action)
