    if currentPlayer.color ~= player.color then
        takeTopResourceCard(player)
        broadcastToAll(player.name .. " drew the top card of the Resource deck from the Djinn " .. djinn.name .. " for " .. currentPlayer.name .. " ending thier movement on a Market Tile.", player.color)
    end
end

function takeTopResourceCard(player)
    local resourceObj = getObjectFromGUID(savedData.resourceDeck).takeObject()
    resourceObj.setLock(true)
    resourceObj.interactable = false
    Wait.frames(function()
        prepareResource(resourceObj.guid)
        table.insert(player.resources, resourceObj.guid)
        Wait.frames(function()
            arrangePlayerResources(player)
        end, 5)
    end, 10)
