function djinnActivateTiles(player, action)
    player.action = action
    player.action.selectedTiles = {}
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if _G[action.djinn.tileReqFunc](tile, player) then
                table.insert(action.selectedTiles, tile)
                colorTile(tile, colors.pink)
