function djinnReadyUp(djinnName, player, toggleOff)
    local djinn = checkDjinn(djinnName, player)
    if djinn ~= nil and not djinn.used 
        and toggleOff ~= true
        and #checkDjinnCost(player, djinn.cost) ~= 0 then
