function djinnDoubleBlue(player, action)
    player.blueMult = 2
    if #getFakirs(player) == 0 then
        resolveBlueAction(player, player.action)
    end
    spendBroadcastDjinnPaidUsed(player, action, "to double the amount of GCs for their Builders")
end

function djinnDoubleAssassin(player, action)
    player.selectTwoMeeples = true
    spendBroadcastDjinnPaidUsed(player, action, "to be able to kill two Meeples with Assassins")
end

function checkAdjacentTile(tile, player)
    local adjacentTile = player.adjacentTile
    local rowNum = math.abs(adjacentTile.row - tile.row)
    local colNum = math.abs(adjacentTile.col - tile.col)
    if (rowNum == 1 and colNum == 0)
        or (rowNum == 0 and colNum == 1)
        or (rowNum == 1 and colNum == 1) then
        return true
    else
        return false
    end
end

function djinnMovePalace(player, action)
    spendBroadcastDjinnPaidUsed(player, action, "place a Palace to an adjacent Tile")
    placePalace(player, action.tile)
end

function djinnMovePalmTree(player, action)
    spendBroadcastDjinnPaidUsed(player, action, "place a Palm Tree to an adjacent Tile")
    placePalmTree(player, action.tile)
end

function djinnDraw3Pick1Djinn(player, action)
    player.busy = true
    player.drawnDjinns = {}
    for d = 1, 3 do
        local djinnObj = getObjectFromGUID(savedData.djinnDeck).takeObject()
        djinnObj.setLock(true)
        djinnObj.interactable = false
        Wait.frames(function()
            prepareDjinn(djinnObj.guid)
            table.insert(player.drawnDjinns, djinnObj.guid)
        end, 10)
    end
    Wait.frames(function()
        arrangeObjs(player.drawnDjinns, player.board, 2.75, 7.5, 3)
    end, 10)            
    spendBroadcastDjinnPaidUsed(player, action, "to draw 3 Djinns and gain one")
end

function isDrawnDjinn(player, djinnGuid)
    if player.drawnDjinns ~= nil then
        for d, djinn in ipairs(player.drawnDjinns) do
            if djinn == djinnGuid then
                return true
            end
        end
        return false
    end
    return false
end

function spendBroadcastDjinnPaidUsed(player, action, reason)
    spendElderFakirs(player, action.cost)
    broadcastToAll(player.name .. " paid " .. getCostString(action.cost, true) .. " " .. reason .. " using the Djinn " .. action.djinn.name .. ".", player.color)
    action.djinn.used = true
    if action.djinn.ready == true then
        action.djinn.ready = false
    end
    selectDjinn(player)
end 

--Solo Mode
function rollYazidDice(yazid)
    broadcastToAll("Rolling dice for Yazid the First.", messageColor)
    savedData.phase = 1
    resetDicePosition()
    for d, dice in ipairs({soloDice.row, soloDice.col}) do
        local diceObj = getObjectFromGUID(dice)
        diceObj.setLock(false)
        Wait.time(function()
            diceObj.randomize()
            Wait.time(function()
                diceObj.randomize()
                Wait.time(function()
                    diceObj.setLock(true)
                    assignDiceTilePosition(d)
                end, gameSpeed/2)
            end, gameSpeed/2)
        end, gameSpeed)
    end
    Wait.time(function()
        local row = getDiceObjRow().getRotationValue()
        local col = getDiceObjCol().getRotationValue()
        local yazidColor = getTransparentColor(yazid.color)
        local targetedTiles = {}
        if row ~= 6 then
            table.insert(targetedTiles, savedData.board[6-row][col])
            computerExpress("Blue", "(:")
        else
            computerExpress("Blue", "[:")
            table.insert(targetedTiles, savedData.board[1][col])
            table.insert(targetedTiles, savedData.board[5][col])
        end
        local nonEmptyTiles = {}
        local emptyTiles = 0
        for t, tile in ipairs(targetedTiles) do
            colorTile(tile, getTransparentColor(yazid.color))
            if #tile.meeples == 0 then
                emptyTiles = emptyTiles + 1
            else
                table.insert(nonEmptyTiles, tile)
            end
        end
        yazid.total = row+col
        if emptyTiles == 0 then
            yazid.selectedTiles = nonEmptyTiles
            checkOutbidYazid(yazid)
        else
            local selectedTiles = {}
            local broadcastString = "Yazid the First cannot target empty Tiles. Choose " .. emptyTiles
            if row == 6 then
                if emptyTiles == 2 then
                    broadcastString = broadcastString .. " Tiles"
                else
                    broadcastString = broadcastString .. " Tile"
                end
                selectedTiles = highlightNonEmptyTiles(col, false)
                broadcastString = broadcastString .. " in any other "
            else
                broadcastString = broadcastString .. " Tile"
                selectedTiles = highlightNonEmptyTiles(col, true)
                if #selectedTiles == 0 then
                    selectedTiles = highlightNonEmptyTiles(0, false)
                    broadcastString = broadcastString .. " in any other "
                else
                    broadcastString = broadcastString .. " in the same "
                end
            end
            broadcastString = broadcastString .. "column to target."
            local player = getPlayerFromColor("Pink")
            broadcastToPlayer(broadcastString, player.color)
            player.action = {
                tileFunc = 'chooseYazidTiles',
                amount = #targetedTiles,
                infoTxt = "Choose " .. emptyTiles .. " Tile(s) for " .. yazid.name,
                nonEmptyTiles = nonEmptyTiles,
                selectedTiles = selectedTiles
            }
            showPlayerUI(player)
            computerExpress("Blue", "/:")
        end
    end, gameSpeed*2.5)
end

function highlightNonEmptyTiles(col, equal)
    local tiles = {}
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if ((col ~= c and not equal) or (col == c and equal)) 
                and #tile.meeples > 0 then
                table.insert(tiles, tile)
                colorTile(tile, colors.white)
            end
        end
    end
    return tiles
end

function chooseYazidTiles(player, action)
    for t, tile in ipairs(action.nonEmptyTiles) do
        if tile.guid == action.tile.guid then
            table.remove(action.nonEmptyTiles, t)
            colorTile(action.tile, colors.white)
            return
        end
    end
    table.insert(action.nonEmptyTiles, action.tile)
    colorTile(action.tile, colors.blue)
    if #action.nonEmptyTiles == action.amount then
        local yazid = savedData.yazid
        player.action = nil
        resetTileColors()
        for t, tile in ipairs(action.nonEmptyTiles) do
            colorTile(tile, getTransparentColor(yazid.color))
        end
        yazid.selectedTiles = action.nonEmptyTiles
        checkOutbidYazid(yazid, action.nonEmptyTiles)
    end
end

function checkOutbidYazid(yazid)
    local total = yazid.total
    local player = getPlayerFromColor("Pink")
    hidePlayerUi(player)
    if countGold(player) >= total and not yazid.final then
        player.action = {
            infoTxt = "or",
            passTxt = "Pay " .. total .. " GC To Go First",
            passFunc = "playerGoesFirst",
            passAltTxt = "Pass",
            cost = total
        }
        showPlayerUI(player)
        broadcastToPlayer("Choose if you want to pay to go first or pass.", player.color)
    else
        startYazidTurn(yazid)
    end
end

function playerGoesFirst(player, action, alt)
    if alt then
        moveFirstPlayerToken(savedData.yazid)
        resetTileColors()
        startYazidTurn(savedData.yazid)
    else
        computerExpress("Blue", "o:")
        Wait.time(function()
            computerExpress("Blue", "(:")
        end, computerSpeed*2)
        savedData.yazid.selectedTiles = {}
        moveFirstPlayerToken(player)
        resetTileColors()
        savedData.yazid.tookTurn = false
        spendGoldCoins(player, action.cost)
        broadcastToAll(player.name .. " paid " .. action.cost .. " GC to go first.", player.color)
        startPlayerTurn("Pink")
    end
end

function startYazidTurn(yazid)
    computerExpress("Blue", "(:")
    broadcastToAll("Player " .. yazid.name .. "'s Turn.", yazid.color)
    savedData.currentTurn = "Blue"
    resetTileColors()
    resolveYazidTiles(yazid)
end

function resolveYazidTiles(yazid)
    if #yazid.selectedTiles == 0 then
        endYazidTurn(yazid)
    else
        local tile = table.remove(yazid.selectedTiles, 1)
        colorTile(tile, getTransparentColor(yazid.color))
        local meepleGroups = {
            {name = "Yellow Vizier", meeples = {}},
            {name = "White Elder", meeples = {}},
            {name = "Green Merchant", meeples = {}},
            {name = "Blue Builder", meeples = {}},
            {name = "Red Assassin", meeples = {}}
        }
        if #tile.meeples == 1 then
            computerExpress("Blue", "):")
        elseif #tile.meeples == 2 then
            computerExpress("Blue", "|:")
        elseif #tile.meeples == 3 then
            computerExpress("Blue", "(:")
        else
            computerExpress("Blue", "[:")
        end
        for m, meeple in ipairs(tile.meeples) do
            for g, group in ipairs(meepleGroups) do
                if group.name == getObjectFromGUID(meeple).getName() then
                    table.insert(group.meeples, meeple)
                    break
                end
            end
        end
        tile.meeples = {}
        for g = #meepleGroups, 1, -1 do
            if #meepleGroups[g].meeples == 0 then
                table.remove(meepleGroups, g)
            end
        end
        clearYazidTile(yazid, tile, meepleGroups)
    end
end

function clearYazidTile(yazid, tile, meepleGroups)
    if #meepleGroups == 0 then
        if tile.camel == nil then
             controlTile(yazid, tile)
        elseif getCamelColor(tile.camel) == "Pink" then
            activateDjinn("Marid", getCamelPlayer(tile.camel))
        end
        resolveYazidTiles(yazid)
    else
        local meepleGroup = table.remove(meepleGroups, 1)
        local meepleObj = getObjectFromGUID(meepleGroup.meeples[1])
        local meeplePos = meepleObj.getPosition()
        moveHand(yazid, meeplePos)
        Wait.time(function()
            highLightOn(yazid, true, meepleObj.guid)
            for m, meeple in ipairs(meepleGroup.meeples) do
                local obj = getObjectFromGUID(meeple)
                local pos = obj.getPosition()
                pos.y = pos.y + 2
                obj.setPositionSmooth(pos, false, true)
            end
            Wait.time(function()
                moveHand(yazid, meeplePos, true)
                highLightOff(yazid, true, meepleObj.guid)
                if resolveYazidMeeples(yazid, tile, meepleGroup) then
                    clearYazidTile(yazid, tile, meepleGroups)
                end
            end, computerSpeed/2)
        end, computerSpeed/2)
    end
end

function resolveYazidMeeples(yazid, tile, meepleGroup)
    if meepleGroup.name == "Yellow Vizier" 
        or meepleGroup.name == "White Elder"
        or meepleGroup.name == "Red Assassin" then
        collectMeeple(yazid, meepleGroup.meeples)
        if meepleGroup.name == "Red Assassin" then
        local redNum = #getMeeples(yazid, "Red Assassin")
            activateDjinn("Nekir")
            if redNum >= 7 then
                showYazidWinner(yazid, "has collected " .. redNum .. " Red Assassins")
                return false
            end
        end
    elseif meepleGroup.name == "Green Merchant" then
        yazid.busy = true
        takeResources(yazid, meepleGroup.meeples, true)
    elseif meepleGroup.name == "Blue Builder" then
        local blueTiles = #getAdjacentColorTiles(tile, "Blue", true)
        local total = #meepleGroup.meeples * blueTiles
        returnMeeples(meepleGroup.meeples)
        local player = getPlayerFromColor("Pink")
        if total > countGold(player) then
            showYazidWinner(yazid, "used " .. #meepleGroup.meeples .. " Blue Builders times " .. blueTiles .. " Blue Tiles to charge " .. player.name .. " " .. total .. " GC when they couldn't pay")
            return false
        else
            broadcastToAll(yazid.name ..  " used " .. #meepleGroup.meeples .. " Blue Builders times " .. blueTiles .. " Blue Tiles to charge " .. player.name .. " " .. total .. " GC.", yazid.color)
            spendGoldCoins(player, total)
        end
    end
    return true
end

function takeYazidDjinn(yazid)
    local fakirCount = #getFakirs(yazid)
    local elderCount = #getMeeples(yazid, "White Elder")
    local cost = nil
    if fakirCount >= 1 and elderCount >= 1 then
        cost = {1,1}
    elseif elderCount >= 2 then
        cost = {2,0}
    end
    if cost ~= nil then
        local djinn = takeDjinnFromRow(yazid, nil, true)
        if djinn ~= nil then
            setDjinnColor(colors.solidGold)
            local djinnObj = getObjectFromGUID(djinn)
            moveHand(yazid, djinnObj.getPosition())
            Wait.time(function()
                highLightOn(yazid, nil, djinn)
                Wait.time(function()
                    highLightOff(yazid, nil, djinn)
                    setDjinnColor("")
                    moveHand(yazid, djinnObj.getPosition(), true)
                    broadcastToAll(yazid.name .. " spent " .. getCostString(cost, true) ..  " to gain the Djinn " .. djinnObj.getName() .. ".", yazid.color)
                    spendElderFakirs(yazid, cost)
                    arrangePlayerDjinns(yazid)
                    djinnObj.UI.setAttribute(djinn .. "Btn", "color", "")
                end, computerSpeed/2)
            end, computerSpeed/2)
            if #yazid.djinns == 6 then
                Wait.time(function()
                    showYazidWinner(yazid, "gained a 6th Djinn")
                end, computerSpeed*2.5)
                return nil
            else
                return 1
            end
        end
    end
    return 0
end

function endYazidTurn(yazid)
    if yazid.busy then
        Wait.frames(function()
            endYazidTurn(yazid)
        end, 10)
        return
    end
    local wait = takeYazidDjinn(yazid)
    if wait ~= nil then
        Wait.time(function()
            resetTileColors()
            moveHand(yazid)
            computerExpress("Blue", "(:")
            if yazid.tookTurn and not yazid.final then
                startPlayerTurn("Pink")
            else
                yazid.final = false
                setNextTurn()
            end
        end, gameSpeed+wait)
    end
end

function startPlayerTurn(color)
    savedData.currentTurn = color
    currentPlayer = getPlayerFromColor(color)
    checkActiveDjinns(currentPlayer)
    checkActiveItems(currentPlayer)
    if checkAnyMovedLeft() then
        savedData.phase = 2
    else
        if not savedData.endGame then
            savedData.endGame = true
            broadcastToAll("NO MOVES LEFT, END GAME TRIGGERED!", messageColor)
        end
        broadcastToPlayer("No Moves Left, skipping main action.", savedData.currentTurn)
        endRound(currentPlayer, nil, true)
    end
end

function showYazidWinner(yazid, reason)
    savedData.currentTurn = nil
    broadcastToAll(yazid.name .. " " .. reason .. " and won the game. Better luck next time!", yazid.color)
    toggleTurnHighlight(yazid, true)
    getObjectFromGUID(yazid.turnHighlight).setColorTint(Color.new({r=1, g=0.84, b=0, a=0.5}))
    local player = getPlayerFromColor("Pink")
    player.action = {infoTxt = "Better Luck Next Time!"}
    showPlayerUI(player)
end

function showWinnerSolo()
    local ranks = {
        {score = 190, face = "|:", title = "Insufficient Iznogoud", elderMsg = "At least, you survived."},
        {score = 220, face = "):", title = "Junior Jafaar", elderMsg = "You made it to the throne! ... until the next coup, that is."},
        {score = 250, face = "D:", title = "Ambitious Al-Rachid", elderMsg = "The Sultanate is yours! This was almost too easy."},
        {score = 999, face = "|X", title = "Splendid Saladin", elderMsg = "Not only did you take the throne, but people love you. You still wonder how you did it, but whatever works."}
    }
    local player = getPlayerFromColor("Pink")
    toggleTurnHighlight(player, true)
    getObjectFromGUID(player.turnHighlight).setColorTint(Color.new({r=1, g=0.84, b=0, a=0.5}))
    for r, rank in ipairs(ranks) do
        if player.scoreTotal <= rank.score then
            computerExpress("Blue", rank.face)
            player.action = {infoTxt = rank.title}
            showPlayerUI(player)
            broadcastToAll(player.name .. " scored " .. player.scoreTotal .. " and achieved the title '" .. rank.title .. "'. Congratulations!", player.color)
            broadcastToAll(rank.elderMsg, messageColor)
            return
        end
    end
end

function getDiceObjRow()
    return getObjectFromGUID(soloDice.row)
end

function getDiceObjCol()
    return getObjectFromGUID(soloDice.col)
end

function assignDiceTilePosition(diceNum)
    if diceNum == 1 then
        setDicePosition(soloDice.row, {x = -24.2, y = 2.54, z = -14.4}, true)
    else
        setDicePosition(soloDice.col, {x = -18, y = 2.54, z = -20.6}, false)
    end
end

function setDicePosition(dice, start, row)
    local diceObj = getObjectFromGUID(dice)
    local offset = 7.2
    local diceNum = diceObj.getRotationValue()
    local rot = diceObj.getRotationValues()[diceNum].rotation
    if row then
        start.z = start.z + offset*(diceNum-1)
    else
        start.x = start.x + offset*(diceNum-1)
    end
    diceObj.setRotationSmooth(rot, false, true)
    diceObj.setPositionSmooth(start, false, true)
end

function resetDicePosition()
    getObjectFromGUID(soloDice.row).setPositionSmooth({x = -26.5, y = 2.54, z = 0}, false, true)
    getObjectFromGUID(soloDice.col).setPositionSmooth({x = 0, y = 2.54, z = -23}, false, true)
end

