function broadcastToPlayerScore(points, reason, player)
    if savedData.endGame then
        if points == nil then
            broadcastToPlayer(msg, player.color)
        else
            broadcastToPlayer("Scored " .. points .. " points for " .. reason .. ".", player.color)
        end
    end
end

function startScoring()
    local waitT = 1
    if savedData.endGame then
        savedData.currentTurn = nil
        turnHighlight()
        broadcastToAll("SCORING START", messageColor)
    else
        waitT = 0
    end
    local scoringTable = {
        {name = "Gold Coins", func = 'scoreGoldCoins', num = 1},
        {name = "Yellow Viziers", func = 'scoreYellowViziers', num = 2},
        {name = "Purple Artisans", func = 'scorePurpleArtisans', num = 3},
        {name = "White Elders", func = 'scoreWhiteElders', num = 4},
        {name = "Djinn Cards", func = 'scoreDjinnCards', num = 5},
        {name = "Palm Trees", func = 'scorePalmTrees', num = 6},
        {name = "Palaces", func = 'scorePalaces', num = 7},
        {name = "Tile Control", func = 'scoreTileControl', num = 8},
        {name = "Items", func = 'scoreItems', num = 9},
        {name = "Resource Sets", func = 'scoreResources', num = 10}
    }
    for p, player in ipairs(savedData.players) do
        player.scores = {'','','','','','','','','',''}
        player.scoreTotal = 0
    end
    if not expansionArtisans then
        table.remove(scoringTable, 9)
        table.remove(scoringTable, 3)
        for p, player in ipairs(savedData.players) do
            player.scores[3] = '-'
            player.scores[9] = '-'
        end
    end
    if not showingScore then
        showScoreSheet()
    end
    for s, scoring in ipairs(scoringTable) do
        Wait.time(function()
            for p, player in ipairs(savedData.players) do
                local points = 0
                if scoring.func ~= '' then
                    points = _G[scoring.func](player)
                end
                player.scores[scoring.num] = points
                player.scoreTotal = player.scoreTotal + points
                broadcastToPlayerScore(points, scoring.name, player)
                updateScoreSheet(player)
            end
        end, scoringSpeed*s*2*waitT)
    end
    if savedData.endGame then
        Wait.time(function()
            if gameMode == 1 then
                showWinner()
            else
                showWinnerSolo()
            end
        end, scoringSpeed*2*(#scoringTable+1))
    end
end

function liftGuid(guid, duration)
    if not savedData.endGame then
        return
    end
    if duration == nil then
        duration = scoringSpeed
    end
    local obj = getObjectFromGUID(guid)
    local pos = obj.getPosition()
    pos.y = pos.y + 2
    obj.setPositionSmooth(pos, false, true)
    Wait.time(function()
        pos.y = pos.y - 2
        obj.setPositionSmooth(pos, false, true)
    end, duration)
end

function scoreGoldCoins(player)
    if savedData.endGame then
        Wait.frames(function()
            setVisiblity(getObjectFromGUID(player.board), "goldTxt", "")
        end, 10)
        for c, coin in ipairs(player.coins) do
            local coinObj = getObjectFromGUID(coin)
            local rot = coinObj.getRotation()
            if rot.z ~= 0 then
                local pos = coinObj.getPosition()
                pos.y = pos.y - 0.17
                rot.z = 0
                coinObj.setRotation(rot)
                coinObj.setPosition(pos)
            end
            liftGuid(coin)
        end
    end
    return player.goldTotal
end

function scoreYellowViziers(player)
    local lowerPlayerCount = 0
    local vizierCount = #getMeeples(player, "Yellow Vizier", true)
    for p, opponent in ipairs(savedData.players) do
        if #getMeeples(opponent, "Yellow Vizier") < vizierCount then
            lowerPlayerCount = lowerPlayerCount + 1
        end
    end
    if checkDjinn("Jafaar", player) ~= nil then
        vizierCount = vizierCount*3
    end
    local majorityBonus = 10*lowerPlayerCount
    if majorityBonus > 0 then
        broadcastToPlayerScore(majorityBonus, "having more Yellow Viziers than " .. lowerPlayerCount .. " players", player)
    end
    return vizierCount + 10*lowerPlayerCount
end

function scorePurpleArtisans(player)
    local mult = 3
    local artisanCount = #getMeeples(player, "Purple Artisan", true)
    for p, opp in ipairs(savedData.players) do
        if opp.color ~= player.color 
            and #getMeeples(opp, "Purple Artisan") >= artisanCount then
            mult = 2
            break
        end
    end
    if checkDjinn("Ptah", player) ~= nil then
        mult = mult + 2
    end
    if mult == 3 then
        broadcastToPlayerScore(nil, "Your Purple Artisans are worth 3 points each for having the majority.", player)
    end
    return artisanCount*mult
end

function scoreItems(player)
    local add = 0
    if checkDjinn("Geb", player) ~= nil then
        add = 3
    end
    local pointTotal = 0
    for i, item in ipairs(player.items) do
        if item.name == 'Precious Item' then
            pointTotal = pointTotal + item.points + add
        end
    end
    return pointTotal
end

function scoreWhiteElders(player)
    local elderCount = #getMeeples(player, "White Elder", true)
    if checkDjinn("Shamhat", player) ~= nil then
        elderCount = elderCount*2
    end
    return elderCount*2
end

function scoreDjinnCards(player)
    local pointTotal = 0
    for d, djinn in ipairs(player.djinns) do
        pointTotal = pointTotal + djinn.points
        liftGuid(djinn.guid)
    end 
    if checkDjinn("Wilwit", player) ~= nil then
        pointTotal = pointTotal + (#player.djinns-1)*5
    end
    return pointTotal
end

function getTransparentColor(color)
    local transColors = {
        {color = "Brown", trans = "rgba(0.65,0.16,0.16,0.5)"},
        {color = "White", trans = "rgba(0,0,0,0.5)"},
        {color = "Pink", trans = "rgba(1,0.75,0.8,0.5)"},
        {color = "Orange", trans = "rgba(1,0.5,0,0.5)"},
        {color = "Blue", trans = "rgba(0,0,1,0.3)"}
    }
    for c, col in ipairs(transColors) do
