function scorePalmTrees(player)
    return scoreBuilding(player, "Palm Tree", 3)
end

function scorePalaces(player)
    return scoreBuilding(player, "Palace", 5)
end

function scoreBuilding(player, name, points)
    local buildingTotal = 0
    local lakeTotal = 0
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if tile.camel ~= nil and getCamelColor(tile.camel) == player.color then
                local found = false
                local mult = #getAdjacentColorTiles(tile, "Lake")
                for b, building in ipairs(tile.buildings) do
                    if getObjectFromGUID(building).getName() == name then
                        buildingTotal = buildingTotal + 1 + mult
                        liftGuid(building)
                        found = true
                    end
                end
                if found then
                    flashTileColor(tile, player.color)
                end
            end
        end
    end
    if lakeTotal > 0 then
        broadcastToPlayerScore(nil, "Scored double points for " .. lakeTotal .. " " .. name .. "(s) next to the Great Lake.", player)
    end 
    if name == "Palm Tree" and checkDjinn("Haurvatat", player) ~= nil then
        return buildingTotal*5
    end
    return buildingTotal*points
end

function scoreTileControl(player)
    local pointTotal = 0
    local cityCount = 0
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if tile.camel ~= nil and getCamelColor(tile.camel) == player.color then
                if string.match(getObjectFromGUID(tile.camel).getName(), 'Tent') then
                    local redCount = #getAdjacentColorTiles(tile, "Red")
                    pointTotal = pointTotal + redCount
                    if savedData.endGame then
                        broadcastToPlayer("Scored " .. redCount .. " points for Red Tiles adjacent to your Tent.", player.color)
                    end
                end
                if tile.name == 'Fabulous City' then
                    cityCount = cityCount + 1
                else
                    pointTotal = pointTotal + tile.points
                end
                liftGuid(tile.camel)
                flashTileColor(tile, player.color)
            end
        end
    end
    if cityCount > 0 then
        local cityPoints = 5*cityCount*cityCount
        broadcastToPlayerScore(cityPoints, "controlling " .. cityCount .. " Fabulous City(s)", player)
        pointTotal = pointTotal + cityPoints
    end
    return pointTotal
end

function scoreResources(player, set)
    local pointTotal = 0
    local setTable = {1,3,7,13,21,30,40,50,60}
    local scoredResources = table.copy(player.resources)
    local fakirCount = 0
    local endGame = true
    if set == nil then
        for c, card in ipairs(player.resources) do
            getObjectFromGUID(card).setHiddenFrom({})
        end
    else
        scoredResources = table.copy(set)
        endGame = false
    end
    for r = #scoredResources, 1, -1 do
        if getObjectFromGUID(scoredResources[r]).getName() == 'Fakir' then
            fakirCount = fakirCount + 1
            table.remove(scoredResources, r)
        else
            if endGame then
                liftGuid(scoredResources[r])
            end
        end
    end
    local wildResources = 0
    if checkDjinn("Al-Amin", player) ~= nil then
        wildResources = math.floor(fakirCount/2)
    end
    while(#scoredResources ~= 0) do
        local setNum = 0
        for r, resource in ipairs(resources) do
            for c, card in ipairs(scoredResources) do
                if getObjectFromGUID(card).getName() == resource then
                    table.remove(scoredResources, c)
                    setNum = setNum + 1
                    break
                end
            end
        end
        while setNum < 9 and wildResources > 0 do
            setNum = setNum + 1
            wildResources = wildResources - 1
        end
        if setNum > 0 then
            pointTotal = pointTotal + setTable[setNum]
        end
    end
    return pointTotal
end

