function textEntered(player, score, id)
    local score = tonumber(score)
    if score ~= nil then
        local playerColor = string.split(id, "|")[1]
        local scoreNum = tonumber(string.split(id, "|")[3])
        local player = getPlayerFromColor(playerColor)
        player.scores[scoreNum] = score
        local scoreTotal = 0
        for s, scr in ipairs(player.scores) do
            if tonumber(scr) ~= nil then
                scoreTotal = scoreTotal + scr
            end
        end
        UI.setAttribute(playerColor .. "TotalTxt", "text", scoreTotal)
    end
end

function textDoneEntered(player, score, id)
    UI.setAttribute(id, "text", score)
end

function updateScoreSheet(player)
    local scoreTotal = 0
    for s, scr in ipairs(player.scores) do
        UI.setAttribute(player.color .. "|score|" .. s .. "Txt", "text", scr)
    end
    UI.setAttribute(player.color .. "TotalTxt", "text", player.scoreTotal)
end

function liftAndScore(player, group, score)
    for t, item in ipairs(group) do
        local object = getObjectFromGUID(item)
        local pos = object.getPosition()
        Wait.time(function()
            object.setPositionSmooth({x = pos.x, y = pos.y + 2, z = pos.z}, false, true) 
        end, scoringSpeed)
        Wait.time(function() object.setPositionSmooth({x = pos.x, y = pos.y, z = pos.z}, false, true) end, scoringSpeed*2)
    end
    if player ~= nil then
        Wait.time(function() 
            if score > 0 then
                UI.setAttribute("display" .. player.color .. "Txt", "text", player.score .. "(+" .. score ..") Points")
            else
                UI.setAttribute("display" .. player.color .. "Txt", "text", player.score .. "(" .. score ..") Points")
            end
        end, scoringSpeed)
        Wait.time(function()
            player.score = player.score + score
            UI.setAttribute("display" .. player.color .. "Txt", "text", player.score .. " Points")
        end, scoringSpeed*3-scoringSpeed/3)
    end
end

function showWinner()
    local winners = nil
    for p, player in ipairs(savedData.players) do
        if winners == nil or player.scoreTotal > winners[1].scoreTotal then
            winners = {player}
        elseif player.scoreTotal == winners[1].scoreTotal then
            table.insert(winners, player)
        end
    end
    local winnerString = ""
    for i, winner in ipairs(winners) do
        computerExpress(winner.color, "[:")
        if i > 1 then
            winnerString = winnerString .. ", "
        end
        if i == #winners and i > 1 then
            winnerString = winnerString .. "and "
        end
        winnerString = winnerString .. winner.name
        getObjectFromGUID(winner.turnHighlight).setColorTint(Color.new({r=1, g=0.84, b=0, a=0.5}))
        toggleTurnHighlight(winner, true)
        winner.action = {infoTxt = "Congratulations!"}
        showPlayerUI(winner)
    end
    local color = winners[1].color
    if #winners > 1 then
        color = "Black"
    end
    broadcastToAll(winnerString .. " won the game with " .. winners[1].scoreTotal .. " points. Congratulations!", color)
end

