function incrementValue(player, mouse, id)
    if mouse ~= '-1' and mouse ~= '0' then
        return
    end
    local tableObject = getObjectFromGUID(tableGuid)
    local variable = tableObject.UI.getAttribute(id,"var")
    local increment = tableObject.UI.getAttribute(id,"inc")
    _G[variable] = _G[variable] + increment
    tableObject.UI.setAttribute(variable .. "Display", "text", _G[variable .. "s"][_G[variable]].name)
    if _G[variable] == #_G[variable .. "s"] then
        tableObject.UI.hide(variable .. "IncUp")
        tableObject.UI.show(variable .. "IncDown")
    elseif _G[variable] == 1 then
        tableObject.UI.hide(variable .. "IncDown")
        tableObject.UI.show(variable .. "IncUp")
    else
        tableObject.UI.show(variable .. "IncUp")
        tableObject.UI.show(variable .. "IncDown")
    end
    broadcastToAll(tableObject.UI.getAttribute(variable .. "Txt","text") .. " changed to " .. _G[variable .. "s"][_G[variable]].name, messageColor)
    if variable == 'tableDesign' then
        changeTable()
    else
        toggleExpansions(nil, variable)
        if variable == 'gameMode' then
            if gameMode == 1 then
                if alwaysScoring then
                    toggleValue(nil, '-1', "alwaysScoringTgl")
                end
                if hiddenInformationOption == 3 then
                    hiddenInformationOption = 2
                    incrementValue(nil, '-1', "hiddenInformationOptionIncDown")
                end
            elseif gameMode ~= 1 then
                if not alwaysScoring then
                    toggleValue(nil, '-1', "alwaysScoringTgl")
                end
                if hiddenInformationOption ~= 3 then
                    hiddenInformationOption = 2
                    incrementValue(nil, '-1', "hiddenInformationOptionIncUp")
                end
            end
        end
    end
end

