    else
        _G[toggle] = true
        tableObject.UI.setAttribute(toggle .. "Tgl", "colors", toggleColor(true))
        tableObject.UI.setAttribute(toggle .. "Tgl", "isOn", "true")
        broadcastToAll(tableObject.UI.getAttribute(toggle .. "Btn","text") .. " Enabled", messageColor)
    end
    toggleExpansions()
end

function toggleExpansions(sit, variable)
    for r, reference in ipairs(rulebooks) do
        if reference.toggle ~= nil then
            for g, guid in ipairs(reference.guids) do
                if _G[reference.toggle] then
                    getObjectFromGUID(guid).setInvisibleTo({})
                else
                    getObjectFromGUID(guid).setInvisibleTo(getPlayerList(nil, true))
                end
            end
        end
    end
    if savedData.phase ~= 0 then
        return
    end
    for t, tile in ipairs(tiles) do
        local enabled = (
            tile.set == 'base' 
            or (tile.set == 'artisans' and expansionArtisans)
            or (tile.set == 'whims' and expansionWhims))
        for g, guid in ipairs(tile.guids) do
            toggleItem(guid, enabled)
        end
    end
    toggleItem(tribes[6].bagGuid, expansionArtisans)
    toggleItem(tribes[3].bagGuid, not altGreenMerchant)
    toggleItem(tribes[3].altBagGuid, altGreenMerchant)
    for d, djinn in ipairs(djinnDecks) do
        toggleItem(djinn.guid, djinn.toggle == nil or _G[djinn.toggle])
    end
    toggleItem(itemsBagGuid, expansionArtisans)
    for i, item in ipairs(items) do
        toggleItem(item.guid, expansionArtisans)
        toggleItem(item.clone, expansionArtisans)
    end
    for m, mountain in ipairs(mountainsGuids) do
        toggleItem(mountain, expansionArtisans)
    end
    toggleItem(whimsDeckGuid, expansionWhims)
    toggleItem(whimsArtisanDeckGuid, expansionWhims and expansionArtisans)
    toggleItem(thievesDeckGuid, expansionThieves)
    toggleItem(thiefMadjaarGuid, expansionThieves and expansionArtisans)
    adjustPlayerItems(sit, variable)
    if gameMode > 1 then
        local broadcastString = "Solo mode is not compatible with the "
        if expansionArtisans then
            broadcastToAll(broadcastString .. "Artisans of Naqala Expansion,", messageColor)
            toggleValue(nil, '-1', "expansionArtisansTgl")
        elseif expansionThieves then
            broadcastToAll(broadcastString .. "Thieves of Naqala Expansion,", messageColor)
            toggleValue(nil, '-1', "expansionThievesTgl")
        elseif expansionWhims then
            broadcastToAll(broadcastString .. "Whims of the Sultan Expansion,", messageColor)
            toggleValue(nil, '-1', "expansionWhimsTgl")
        end
    end
end

function adjustPlayerItems(sit, variable)
    if #savedData.players > 0 then
        return
    end
    local playerCount = 0
    for p, player in ipairs(playerInfo) do
        local enable = false
        if isPlayerSeated(player.color) or isComputerEnabled(player.color) then
            if gameMode == 1 or (player.color ~= "Blue" or isPlayerSeated(player.color)) then
                playerCount = playerCount + 1
            end
            enable = true
        end
        toggleItem(player.camelBagGuid, enable)
        toggleItem(player.markerGuid[1], enable and noBidding == 1 and gameMode == 1)
        Wait.frames(function()
            toggleItem(player.markerGuid[2], enable and playerCount == 2 and noBidding == 1 and gameMode == 1)
        end, 5)
        toggleItem(player.tentGuid, enable and expansionArtisans)
        toggleItem(player.camelBagGuid, enable)
        if playerAreaTint and enable then
            local c = player.darkColor
            local m = 0.5
            getObjectFromGUID(player.board).setColorTint(Color.new({r=c.r*m, g=c.g*m, b=c.b*m, a=c.a*1.75}))
        else
            getObjectFromGUID(player.board).setColorTint(Color.new({r=0, g=0, b=0, a=0}))
        end
    end
    toggleItem(bidOrderTrackGuids[1], playerCount < 5 and noBidding == 1 and gameMode == 1)
    toggleItem(bidOrderTrackGuids[2], playerCount == 5 and noBidding == 1 and gameMode == 1)
    toggleItem(turnOrderTrackGuids[1], playerCount < 5 and noBidding == 1 and gameMode == 1)
    toggleItem(turnOrderTrackGuids[2], playerCount == 5 and noBidding == 1 and gameMode == 1)
    if gameMode > 1 then
        getObjectFromGUID(soloRulebookGuid).setInvisibleTo({})
    else
        getObjectFromGUID(soloRulebookGuid).setInvisibleTo(getPlayerList(nil, true))
    end
    toggleItem(soloDice.row, gameMode > 1)
    toggleItem(soloDice.col, gameMode > 1)
    toggleItem(firstPlayerMarkerGuid, noBidding > 1 or gameMode > 1)
    if sit ~= nil and (sit ~= "Blue" or isPlayerSeated("Blue")) then
        if gameMode == 1 and playerCount < 2 and not isPlayerSeated("Blue") then
            incrementValue(nil, '-1', "gameModeIncUp")
            return
        elseif gameMode ~= 1 and playerCount > 1 or isPlayerSeated("Blue")then
            gameMode = 2
            incrementValue(nil, '-1', "gameModeIncDown")
            return
        end
    end
    if gameMode > 1 then
        if not isComputerEnabled("Blue") then
            toggleComputer(nil, nil, tableGuid .. "BlueaddComputerBtn")
        end
        getObjectFromGUID(tableGuid).UI.hide(tableGuid .. "BlueaddComputerBtn")
        getObjectFromGUID(tableGuid).UI.setAttribute("BluecomputerNameTxt", "text", "Yazid the First")
    else
        if isComputerEnabled("Blue") and variable == 'gameMode' then
            toggleComputer(nil, nil, tableGuid .. "BlueaddComputerBtn")
        end
        getObjectFromGUID(tableGuid).UI.show(tableGuid .. "BlueaddComputerBtn")
        getObjectFromGUID(tableGuid).UI.setAttribute("BluecomputerNameTxt", "text", "Blue Dummy")
        getObjectFromGUID(tableGuid).UI.setAttribute(tableGuid .. "BlueaddComputerBtn", "visibility", "Black")
    end
end 

