function leftClick(player, mouse, id)
    local object = getObjectFromId(id)
    local isHost = player.host
    if mouse ~= '-1' and mouse ~= '0' then
        if mouse == '-2' then
            if object.getName() ~= '' then
                local infoString = object.getName()
                if object.getDescription() ~= '' then
                    infoString = infoString .. " : " .. object.getDescription()
                end
                broadcastToColor(infoString, player.color, messageColor)
            end
        end
        return
    end
    local isTurn = false
    player = getPlayerFromColor(player.color, true)
    if player ~= nil then
        isTurn = player.color == savedData.currentTurn
    end
    if player ~= nil or savedData.phase == 0 then
        local run = object.UI.getAttribute(id,"func")
        if run ~= "" and run ~= nil then
            _G[run](player, isTurn, id, isHost)
        end
    end
end

