function scaleDeck(deck)
    if deck ~= nil then
        local pilePos = deck.getPosition()
        pilePos.y = 1.01 + (0.13/24)*deck.getQuantity()
        deck.setPositionSmooth(pilePos, false, true)
    end
end

function scaleBag(bag, maxNum, player, num)
    if player ~= nil then
        local boardObj = getObjectFromGUID(player.board)
        boardObj.UI.setAttribute(player.board .. "bulletsLeftTxt", 'text', bag.getQuantity())
    end
    if num == nil then
        num = bag.getQuantity()
    end
    local scale = bag.getScale()
    local empty = scale.x/6
    local full = scale.x - empty
    scale.y = empty + full/maxNum*num
    bag.setScale(scale)
end

computerTurn = false
function setTurn(color)
    if not computerTurn then
        if isComputerEnabled(color) then
            if gameMode == 1 then
                computerTurn = true
                Turns.enable = false
                broadcastToAll("Player " .. color .. " Dummy's Turn.", color)
                Wait.time(function() 
                    computerTakeTurn(getPlayerFromColor(color))
                end, computerSpeed)
            end
        else
            Turns.turn_color = color
            Turns.enable = true
        end
    end
end

function updateScore()
    Wait.frames(function()
        if (alwaysScoring or showingScore) and not savedData.endGame then
            startScoring()
        end
    end, 10)
end

function setNextTurn()
    if noScripting then
        manualModeWarning = true
        startRound()
        Wait.time(function()
            manualMode(nil, nil, nil, true)
        end, setupSpeed*2)
        return
    end
    updateScore()
    local previousTurn = savedData.currentTurn
    if gameMode > 1 then
        local yazid = savedData.yazid
        savedData.currentTurn = nil
        turnHighlight()
        if yazid.tookTurn then
            if savedData.endGame then
                local player = getPlayerFromColor("Pink")
                if #getMeeples(player, "Yellow Vizier") <= #getMeeples(yazid, "Yellow Vizier") then
                    showYazidWinner(yazid, "does not have less Yellow Viziers than " .. player.name)
                else
                    startScoring()
                end
                return
            end
            broadcastToAll("ROUND START", messageColor)
            startRound()
            rollYazidDice(savedData.yazid)
        else
            yazid.tookTurn = true
            yazid.final = true
            rollYazidDice(savedData.yazid)
        end
        return
    elseif noBidding ~= 1 then
        local nextTurn = 1
        for i, player in ipairs(savedData.players) do
            if player.color == savedData.currentTurn then
                nextTurn = i + 1
                break
            end
        end
        if nextTurn > #savedData.players then
            nextTurn = 1
        end
        savedData.phase = 2
        if not checkAnyMovedLeft() then
            savedData.endGame = true
            broadcastToAll("NO MOVES LEFT, ENDING GAME", messageColor)
        end
        if savedData.turnCount == nil then
            moveFirstPlayerToken(savedData.players[nextTurn])
        end
        if savedData.endGame and savedData.turnCount == #savedData.players then
            startScoring()
            return
        elseif savedData.turnCount == nil
            or (savedData.turnCount == #savedData.players 
                and (noBidding == 2
                    or noBidding == 3 and #savedData.players ~= 2))
            or savedData.turnCount == #savedData.players*2 then
            savedData.turnCount = 1
        else
            savedData.turnCount = savedData.turnCount + 1
        end
        if noBidding == 2 then
            savedData.currentTurn = savedData.players[nextTurn].color
            startRound()
        elseif savedData.turnCount == 1 then
            startRound()
            broadcastToAll("First Player passed right to " .. getPlayerFromColor(savedData.currentTurn).name .. ".", savedData.currentTurn)
            broadcastToAll("ROUND START", messageColor)
            savedData.turnCount = 1
            moveFirstPlayerToken(getPlayerFromColor(savedData.currentTurn))
        else
            savedData.currentTurn = savedData.players[nextTurn].color
        end
    else
        local nextTurnMarker = nil
        if savedData.phase ~= 1 then
            if #savedData.turnOrder > 0 then
                nextTurnMarker = table.remove(savedData.turnOrder, #savedData.turnOrder)
                table.insert(savedData.bidOrder, {
                    color = nextTurnMarker.color,
                    guid = nextTurnMarker.guid
                })
                -- local nextPlayer = getPlayerFromColor(nextTurnMarker.color)
                -- nextPlayer.action = {infoTxt = "Action Phase"}
                -- showPlayerUI(nextPlayer)
                arrangeBidTrack()
            else
                savedData.phase = 1
                if not checkAnyMovedLeft() then
                    savedData.endGame = true
                    broadcastToAll("NO MOVES LEFT, ENDING GAME", messageColor)
                end
                if savedData.endGame then
                    startScoring()
                    return
                else
                    getObjectFromGUID(savedData.turnTrack).UI.show("bannerTxt")
                    broadcastToAll("BIDDING PHASE START", messageColor)
                    startRound()
                end
            end
        end
        if savedData.phase == 1 then
            nextTurnMarker = table.remove(savedData.bidOrder, 1)
            savedData.currentMarker = {
                color = nextTurnMarker.color,
                guid = nextTurnMarker.guid,
                bid = nil
            }
            local nextPlayer = getPlayerFromColor(nextTurnMarker.color)
            nextPlayer.action = {infoTxt = "Bidding Phase"}
            local kumarbi = checkDjinn("Kumarbi", nextPlayer)
            if kumarbi ~= nil and #getFakirs(nextPlayer) ~= 0 then
                broadcastToPlayer("Spend any Fakirs to reduce the cost of your bid by one level each.", nextPlayer.color)
                getObjectFromGUID(kumarbi.guid).UI.setAttribute(kumarbi.guid .. "Btn", "color", colors.solidGold)
                nextPlayer.action.spentFakirs = {}
                nextPlayer.action.infoTxt2 = "Fakirs Spent: 0"
            end
            showPlayerUI(nextPlayer)
            Wait.time(function()
                local markerObj = getObjectFromGUID(nextTurnMarker.guid)
                local pos = markerObj.getPosition()
                pos.y = pos.y + 1.5
                markerObj.setPositionSmooth(pos, false, true)
                Wait.frames(function()              
                    broadcastToPlayer("Choose a spot on the Turn Order Track to bid on.", nextTurnMarker.color)
                end, 30)
            end, setupSpeed)
        end
        savedData.currentTurn = nextTurnMarker.color
    end
    computerTurn = false
    local currentPlayer = getPlayerFromColor(savedData.currentTurn)
    if savedData.currentTurn == previousTurn and not isComputerEnabled(savedData.currentTurn) then
        turnHighlight()
        setTurn(currentPlayer.color)
        currentPlayer.itemUsed = false
    end
    if savedData.phase > 1 then
        startPlayerTurn(savedData.currentTurn)
    end
end

function checkAnyMovedLeft()
    local totalMeeples = 0
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            totalMeeples = totalMeeples + #tile.meeples
            if totalMeeples > 20 then
                return true
            end
        end
    end
    for r, row in ipairs(savedData.board) do
        for c, tile in ipairs(row) do
            if #tile.meeples > 0 then
                moveFound = false
                findLegalMove(tile)
                if moveFound then
                    return true
                end
            end
        end
    end
    return false
end

function keepCurrentPlayerTurn()
    if savedData.manualMode or noScripting then
        return
    end
    if savedData.currentTurn ~= nil 
        and ((isComputerEnabled(savedData.currentTurn) and not computerTurn) 
            or (isPlayerSeated(savedData.currentTurn) and Turns.turn_color ~= savedData.currentTurn)) then
        turnHighlight()
        setTurn(savedData.currentTurn)
    elseif savedData.currentTurn == nil or not isPlayerSeated(savedData.currentTurn) then
        Turns.enable = false
    end
    Wait.frames(keepCurrentPlayerTurn, 30)
end

function turnHighlight()
    for p, player in ipairs(savedData.players) do
        toggleTurnHighlight(player, savedData.currentTurn == player.color)
    end
    if gameMode > 1 then
        toggleTurnHighlight(savedData.yazid, savedData.currentTurn == "Blue")
    end
end

