function broadcastToPlayer(message, playerColor, msgColor)
    if isPlayerSeated(playerColor) then
        if msgColor == nil then
            msgColor = messageColor
        end
        broadcastToColor(message, playerColor, msgColor)
    elseif isComputerEnabled(playerColor) then
        broadcastToAll(message, messageColor)
    end
end

function isPlayerSeated(color, info)
    local playerList = Player.getPlayers()
    for _, player in ipairs(playerList) do
        if (color ~= nil and color == player.color) 
            or (color == nil) then
            if info == nil then
                return true
            else
                return player
            end
        end
    end
    return false
end

