function freezeGuid(guid, freeze, hide)
    if freeze == nil then
        freeze = true
    end
    local object = getObjectFromGUID(guid)
    if object ~= nil then
        if hide then
            object.setColorTint(Color.new({r=1, g=1, b=1, a=0}))
        elseif hide ~= nil then
            object.setColorTint(Color.new({r=1, g=1, b=1, a=1}))
        end
        object.setLock(true)
        object.interactable = not freeze
    end
end

function getTransformPosition(object, pos)
    local cPos = object.getPosition()
    local fVal = object.getTransformForward()
    local rVal = object.getTransformRight()
    local uVal = object.getTransformUp()
    return {
        x = cPos.x + fVal.x*pos.z + rVal.x*pos.x + uVal.x*pos.y,
        y = cPos.y + fVal.y*pos.z + rVal.y*pos.x + uVal.y*pos.y,
        z = cPos.z + fVal.z*pos.z + rVal.z*pos.x + uVal.z*pos.y
    }
end

