function string.split(s, sep)
    local fields = {}
    local sep = sep or " "
    local pattern = string.format("([^%s]+)", sep)
    string.gsub(s, pattern, function(c) fields[#fields + 1] = c end)
    return fields
end

function table.randomize(t)
    local t2 = {};
    while #t > 0 do
        table.insert(t2, table.remove(t, math.random(#t)))
    end
    return t2;
end

function table.sub(t, num)
    local t2 = {}
    while #t > 0 and #t2 < num do
        table.insert(t2, table.remove(t, 1))
    end
    return t2;
end

function table.copy(t)
    local t2 = {}
    for k,v in pairs(t) do
        if type(v) == "table" then
            t2[k] = table.copy(v)
        else
            t2[k] = v
        end
    end
    return t2;
end

--Computer AI
function computerBlink()
    if savedData.manualMode then
        return
    end
    local gameTable = getObjectFromGUID(tableGuid)
    local idleFaces = {"o:", "|:", "s:", "[:", "/:", "}:", "  ", " :", "]:", ":)", ":("}
    for i, p in ipairs(playerInfo) do
        if isComputerEnabled(p.color) then
            local blinking = math.random(1, 2)
            local move = math.random(1, 20)
            local makeFace = math.random(1, 10)
            if makeFace == 1 and savedData.currentTurn ~= p.color then
                local face = math.random(1, #idleFaces)
                computerExpress(p.color, idleFaces[face])
                Wait.time(function()
                    local currentFace = gameTable.UI.getAttribute(tableGuid .. p.color .. "computerIconTxt", "text")
                    if currentFace == idleFaces[face] then
                        computerExpress(p.color, "(:")
                    end
                end, 1)
            end
            if move <= 5 and savedData.currentTurn ~= p.color then
                moveHand(p)
            end
            if blinking == 1 then
                local currentFace = string.sub(gameTable.UI.getAttribute(tableGuid .. p.color .. "computerIconTxt", "text"), 1, 1)
                computerExpress(p.color, currentFace .. " ")
                Wait.time(function() 
                    local currentFace = string.sub(gameTable.UI.getAttribute(tableGuid .. p.color .. "computerIconTxt", "text"), 1, 1)
                    computerExpress(p.color, currentFace .. ":")
                end, 0.1)
            end
        end
    end
    Wait.time(function() computerBlink() end, 2.5)
end

function computerTakeTurn(player)
    return
end

function computerMoveAndClick(player, object, clickFunc, hoverOnFunc, hoverOffFunc)
    if object == nil then
        moveHand(player, nil, true)
    else
        moveHand(player, getObjectFromGUID(object).getPosition(), true)
    end
    Wait.time(function()
        _G[hoverOnFunc](player, 1, object)
        Wait.time(function()
            _G[clickFunc](player, 1, object)
            if hoverOffFunc ~= nil then
                _G[hoverOffFunc](player, 1, object)
                moveHand(player)
            end
            --computerExpress(player.color, "(:")
        end, computerSpeed/2)
    end, computerSpeed/2)
end

function moveHand(player, position, click)
    local info = getPlayerInfoIndexFromId(player.color)
    local hand = getObjectFromGUID(info.compHandGuid)
    local rot = hand.getRotation()
    if position == nil then
        local boardObj = getObjectFromGUID(player.board)
        position = boardObj.getPosition()
        position.x = position.x + math.random(0,20) - 10
        position.z = position.z + math.random(0,20) - 10
        position.y = 0
    end
    position.y = position.y + 1
    hand.setPositionSmooth(position, false, true)
    if click then
        Wait.time(function()
            rot.z = 180
            hand.setRotation(rot)
            Wait.time(function()
                rot.z = 0
                hand.setRotation(rot)
            end, computerSpeed*0.2)
        end, computerSpeed*0.8)
    end
end

function addComputerText(added)
    if added then
        return "Remove Dummy"
    else
        return "Add Dummy"
    end
end

function onPlayerTurn(player)
    if player.color != savedData.currentTurn then
        local player = getPlayerFromColor(savedData.currentTurn)
        if player ~= nil and player.action ~= nil and player.action.passFunc ~= nil then
            clickPass(player, true, 'passBtn')
        end
    end
end

function onPlayerChangeColor(color)
    if gameMode == 1 or color ~= "Blue" then
        adjustComputersForSeatedPlayers()
    elseif savedData.phase ~= 0 then
        if color == "Blue" then
            if isPlayerSeated("Pink") then
                Player.getPlayers()[1].changeColor("Grey")
            else
                Player.getPlayers()[1].changeColor("Pink")
            end
            broadcastToAll("Nice try. You will not be able to claim the throne of Naqala that easily!", messageColor)
        end
    end
    adjustPlayerItems(true)
end

function adjustComputersForSeatedPlayers(hide)
    local seated = false
    local gameTable = getObjectFromGUID(tableGuid)
    local playerList = Player.getPlayers()
    for _, p in ipairs(playerInfo) do
        seated = false
        gameTable.UI.hide(tableGuid .. p.color .. "addComputerBtn")
        for i, player in ipairs(playerList) do
            if p.color == player.color then
                if isComputerEnabled(p.color) then
                    enableComputer(p.color, false)
                end
                seated = true
                break
            end
        end
        if not seated and gameTable ~= nil 
            and ((savedData.currentTurn == nil and hide == nil)
                or (not isComputerEnabled(p.color) and getPlayerFromColor(p.color) ~= nil)) then
            gameTable.UI.show(tableGuid .. p.color .. "addComputerBtn")
        else
            gameTable.UI.hide(tableGuid .. p.color .. "addComputerBtn")
        end
        gameTable.UI.setAttribute(tableGuid .. p.color .. "addComputerBtn", "visibility", "Black")
    end
    
end

function toggleComputer(player, mouse, id)
    local gameTable = getObjectFromGUID(tableGuid)
    local color = getPlayerInfoIndexFromId(id).color
    gameTable.UI.hide(tableGuid .. color .. "addComputerBtn")
    if isComputerEnabled(color) == true then
        computerExpress(color, "D:")
        Wait.time(function() computerExpress(color, "|x") end, 0.33)
        Wait.time(function() enableComputer(color, false) end, 0.5)
    else
        enableComputer(color, true)
        computerExpress(color, "  ")
        Wait.time(function() computerExpress(color, "|") end, 0.11)
        Wait.time(function() computerExpress(color, "|:") end, 0.33)
        Wait.time(function() computerExpress(color, "(:") end, 0.5)
    end
end

function enableComputer(color, enable)
    local gameTable = getObjectFromGUID(tableGuid)
    local colorInfo = getPlayerInfoIndexFromId(color)
    if not enable then
        isComputerEnabled(color, true)
        gameTable.UI.hide(tableGuid .. color .. "computerIconPnl")
        gameTable.UI.hide(color .. "computerNameTxt")
        if not isPlayerSeated(color) then
            gameTable.UI.show(tableGuid .. color .. "addComputerBtn")
        end
    else
        isComputerEnabled(color, true)
        gameTable.UI.show(tableGuid .. color .. "computerIconPnl")
        gameTable.UI.show(color .. "computerNameTxt")
        if savedData.currentTurn == nil and not isPlayerSeated(color) then
            gameTable.UI.show(tableGuid .. color .. "addComputerBtn")
        end
        if savedData.currentTurn ~= nil then
            getPlayerFromColor(color).computer = true
            getPlayerFromColor(color).name = color .. " Dummy"
            if savedData.currentTurn == color then
                computerTakeTurn(getPlayerFromColor(color))
            end
        end
    end
    adjustPlayerItems(color)
    toggleItem(colorInfo.compHandGuid, isComputerEnabled(color))
    gameTable.UI.setAttribute(tableGuid .. color .. "addComputerBtn", "text", addComputerText(isComputerEnabled(color)))
    gameTable.UI.setAttribute(tableGuid .. color .. "addComputerBtn", "visibility", "Black")
end

function toggleItem(guid, enabled, height)
    freezeGuid(guid, true)
    local item = getObjectFromGUID(guid)
    if item == nil then
        return
    end
    local pos = item.getPosition()
    if enabled and pos.y < 0 then
        if height == nil then
            pos.y = pos.y + 3.5
        else
            pos.y = height
        end
    elseif pos.y > 0 and not enabled then
        if height == nil then
            pos.y = pos.y - 3.5
        else
            pos.y = -1
        end
    end 
    item.setPosition(pos)
end

function hoverOnRemoveComputer(player, mouse, id)
    local color = getPlayerInfoIndexFromId(id).color
    computerExpress(color, "o:")
end

function hoverOffRemoveComputer(player, mouse, id)
    local color = getPlayerInfoIndexFromId(id).color
    computerExpress(color, "(:")
end

function isComputerEnabled(color, switch)
    for _, computer in ipairs(savedData.computers) do
        if color == computer.color then
            if switch ~= nil then
                computer.enabled = not computer.enabled
            end
            if computer.enabled then
                return true
            else
                return false
            end
        end
    end
    return false
end

function computerExpress(playerColor, expression)
    local gameTable = getObjectFromGUID(tableGuid)
    local emotes = {"(:", "( ", "o:", "|:", "):", "s:", "[:", "]:", "/:", "}:"}
    if gameTable ~= nil then
        gameTable.UI.setAttribute(tableGuid .. playerColor .. "computerIconTxt", "text", expression)
    end
end
